INSERT INTO `food` (`id`, `name`, `type_id`, `region_id`, `solution`, `ingredient`, `img`) VALUES
('f001', 'ต้มแซ่บปลาคัง', 'C01', 'r3', '-นำกระเทียมดอง กระเทียมสด พริกสด รากผักชี มาโขรกหยาบๆ จากนั้นพักไว้ก่อน\n-ต้มน้ำซุปให้เดือด จากนั้นใส่ส่วนผสมที่โขรกลงไป ปรุงรสด้วย น้ำตาล น้ำปลา น้ำกระเทียมดอง เกลือ และพริกป่น จากนั้นชิมรสชาติ ให้ให้เค็มหวาน\n-ใส่เนื้อปลาลงไปต้ม จากนั้นใส่ใบกระเพราและผักชีฝรั่งลงไป ปิดไฟและใส่น้ำมะนาวลงไปขั้นตอนสุดท้าย\n-สามารถเสริฟรับประทานได้ เมื่อเนื้อปลาสุก', '-เนื้อปลาคัง หั่นเป็นชิ้น 1 ตัว\r\n-น้ำตาลปี๊บ 1 ช้อนโต้ะ\r\n-กระเทียมดอง 2 หัว\r\n-น้ำกระเทียมดอง 2 หัว\r\n-กระเทียมสด 2 ช้อนโต้ะ\r\n-พริกสด 2 ช้อนโต้ะ\r\n-รากผักชี 1 ช้อนโต้ะ\r\n-น้ำซุปหมู 2 ถ้วยตวง\r\n-ผักชีซอย 1 ช้อนโต้ะ\r\n-น้ำมะนาว 4 ช้อนโต้ะ\r\n-เกลือ 1 ช้อนชา\r\n-น้ำปลา 4 ช้อนโต้ะ\r\n-พริกป่น 1 ช้อนโต้ะ\r\n-ใบกระเพรา 2 ช้อนโต้ะ', 'f001'),
('f002', 'ไก่รวนปลาร้า', 'C01', 'r3', '-ตั้งกระทะให้ร้อน จากนั้นใส่น้ำซุปไก่ลงไป ใส่ข่า ตะไคร้ และใบมะกรูดลงไปต้ม จากนั้นใส่เนื้อไก่ลงไปต้ม\r\n-ปรุงรสด้วย ปลาร้า พริก น้ำตาล น้ำปลา น้ำมะนาว จากนั้นเคี้ยวไก่ให้เข้าเนื้อ จนน้ำง่วนลงไป ใช้เวลาประมาณ 15 นาที\r\n-ขั้นตอนสุดท้ายใส่น้ำกะทิลงไป ผัดให้เข้ากันกับส่วนผสมทั้งหมด ไม่ต้องผัดนาน จนกะทิแตกมัน เสริฟใส่จานพร้อมรับประทาน', '-เนื้อไก่สับเป็นชิ้นพอคำ 1 จาน\r\n-น้ำซุปไก่ 2 ถ้วย\r\n-น้ำปลาร้า 1 ช้อนโต้ะ\r\n-น้ำตาล 1 ช้อนชา\r\n-น้ำปลา 1 ช้อนโต้ะ\r\n-น้ำมะนาว 1 ช้อนโต้ะ\r\n-ข่าซอย 1 ช้อนโต้ะ\r\n-ตะไคร้ทุบ หั่นหยาบๆ 1 ช้อนโต้ะ\r\n-ใบมะกรุดซอย 1 ช้อนโต้ะ\r\n-หอมหัวแดงหั่น 2 ช้อนโต้ะ\r\n-พริกบด 1 ช้อนโต้ะ\r\n-กะทิ 2 ช้อนโต้ะ', 'f002'),
('f003', 'พล่าหนวดปลาหมึก', 'C06', 'r3', '-นำหนวดปลามึกและหมูสับไปลวกให้สุก จากนั้นนำใส่ถ้วยสำหรับปรุงรส\r\n-ใส่ น้ำมะนาว น้ำปลา น้ำตาล น้ำซุป พริกสด และน้ำพริกเผา ลงไป คลุกเค้าให้ส่วนผสมเข้ากัน\r\n-ใส่หอมใหญ่ซอย ตะไคร้ซอย ใบหอมซอย ผักชีซอย ใบมะกรูดซอยและใบสาระแหน่ คลุกเคล้าให้เข้ากัน\r\n-เสริฟใส่จาน ทานเป็นกับแกล้มทาน หรือทานกับข้าวสวย ผักสด', '-หนวดปลาหมึก 1 ถ้วย\r\n-ตะไคร้ซอย 2 ช้อนโต้ะ\r\n-หอมใหญ่ซอย 2 ช้อนโต้ะ\r\n-ใบหอมซอย 1 ช้อนโต้ะ\r\n-หมูสับลวก 1 ช้อนโต้ะ\r\n-ผักชีซอย 1 ช้อนโต๊ะ\r\n-ใบมะกรูดซอย 1 ช้อนโต้ะ\r\n-ใบสาระแหน่ 1 ช้อนโต้ะ\r\n-พริกสดซอย 3 ช้อนโต้ะ\r\n-น้ำพริกเผา 1 ช้อนโต๊ะ\r\n-น้ำมะนาว 2 ช้อนโต๊ะ\r\n-น้ำปลา 3 ช้อนโต๊ะ\r\n-น้ำตาล 2 ช้อนโต้ะ\r\n-น้ำซุป 5 ช้อนโต้ะ', 'f003'),
('f004', 'คอหมูย่างนมสด', 'C09', 'r3', '-นำเนื้อหมูมาทุบก่อน เนื้อจะได้นุ่มและดูดซึมเครื่องหมักได้ดี\r\n-นำเนือหมูมาหมัก กับ นมข้นจืด ซอสปรุงรส ซอสหอยนางรม พริกไทย น้ำตาลปี๊บ น้ำเปล่า เกลือ รากผักชี นวดให้ส่วนผสมเข้ากัน และซึมเข้าเนื้อ จากนั้นหมักทิ้งไว้สัก 2 ชั่วโมง\r\n-นำมาย่างด้วยไฟอ่อนๆ ให้สุก เมื่อสุกแล้วก็นำมาหั่นใส่จานทานกับน้ำจิ้มแจ่ว กับข้าวง่ายๆ', '-ซอสปรุงรส 5 ช้อนโต้ะ\r\n-ซอสน้ำมันหอย 2 ช้อนโต้ะ\r\n-พริกไทยป่น 1 ช้อนโต้ะ\r\n-น้ำตาลปี๊บ 1 ช้อนโต้ะ\r\n-น้ำเปล่า 5 ช้อนโต้ะ\r\n-เกลือ 1 ช้อนชา\r\n-รากผักชีบด 1 ช้อนชา\r\n-น้ำจิ้มแจ่ว 1 ถ้วยน้ำจิ้ม', 'f004'),
('f005', 'คอหมูย่างน้ำผึ้ง', 'C09', 'r3', '-นำเนื้อคอหมูย่าง มาทุบก่อน จากนั้น นำมาหมักด้วย น้ำผึ้ง ซอสปรุงรส ซอสน้ำมันหอย พริกไทยป่น และน้ำเปล่า\r\n-นำมานวดให้ส่วนผสมทั้งหมดเข้ากันและซึมเข้าไปในเนื้อหมู หมักทิ้งไว้ 2 ชั่วโมง\r\n-นำมาย่างด้วยไฟอ่อนๆ  เมื่อหมูสุกก็นำมาหั่นเป็นชิ้น ทานคู่กับน้พจิ้มแจ่ว\r\n\r\n', '-สันคอหมูหั่นเป็นชิ้นพอคำ 1 ถ้วย\r\n-น้ำผึ้ง 2 ช้อนขา\r\n-ซอสปรุงรส 2 ช้อนชา\r\n-ซอสน้ำมันหอย 1 ช้อนชา\r\n-พริกไทยป่น 1 ช้อนชา\r\n-น้ำเปล่า 5 ช้อนโต้ะ\r\n-น้ำจิ้มแจ่ว 1 ถ้วยน้ำจิ้ม', 'f005'),
('f006', 'หมูปิ้งสูตรกะทิ', 'C08', 'r3', '-หั่นเนื้อหมุและมันหมู ความหนาประมาณ 3 มิล และกว้างประมาณ 3 เซ็นติเมตร จากนั้นเตรียมน้ำหมักหมู โดย\r\n-ผสม รากผักชี กระเทียม น้ำมันงา น้ำมันหอย ซอสถั่วเหลือง หัวกะทิ น้ำตาล เกลือ และน้ำมันพืช ให้เข้ากัน จากนั้นนำหมูและมันหมูที่หั่นไว้แล้วลงไปนวด\r\n-นวดจนเนื้อหมูและมันหมู ดูดน้ำหมักจนแห้ง จากนั้นเติมแป้งข้าวโพดลงไปนวดกับหมู และนำไปใส่กล่องและแช่ในตู้เย็น 1 คืน\r\n-เมื่อหมักหมู 1 คืนแล้ว นำมาเสียบไม้ โดยไม้ที่ใช้เสียบต้องแช่น้ำก่อน 1 คืน เช่นกัน เพื่อให้ไม้อุ้มน้ำเวลาปิ้งไม้จะไม่ไหม้ เสียบหมูโดยให้เนื้อหมูสลับกับมันหมู ให้ได้ขนาดตามที่เราต้องการ เสียบเสร็จก็นำเนื้อหมูเสียบไม้แล้วแช่ในตู้เย็นต่ออีก 1 คืน จากนั้นเราก็สามารถนำมาปิ้ง เป็นหมูปิ้งกะทิ เงินล้านได้แล้ว เคล็ดลับการปิ้งหมูให้อร่อย คือ ใช้ไฟอ่อน ปิ้งหมูให้สุกเป็นด้านๆ อย่าพลิกหมูกลับไปกลับมา แค่นี้ เราก็จะได้หมูปิ้งที่ หวาน อร่อย แล้ว', '-หมูสันนอก หรือ หมูสันใน 1 กิโล\r\n-มันหมู 1/2 กิโล\r\n-รากผักชีบด 2 ช้อนโต้ะ\r\n-กระเทียมบด 2 ช้อนโต้ะ\r\n-น้ำเปล่า 1 ถ้วย\r\n-น้ำมันพืช 3 ช้อนโต้ะ\r\n-น้ำมันหอย 4 ช้อนโต้ะ\r\n-ซอสถั่วเหลือง 4 ช้อนโต้ะ\r\n-น้ำมันงา 2 ช้อนโต้ะ\r\n-หัวกะทิ 1 ถ้วย\r\n-เกลือ 2 ช้อนโต้ะ\r\n-น้ำตาลปี๊บ 1 ถ้วย\r\n-แป้งข้าวโพด 3 ช้อนโต้ะ\r\n-ไม้จิ้มลูกชิ้น 1 มัด', 'f006'),
('f007', 'ยำขนมจีน,ตำข้าวปุ้น', 'C06', 'r3', '-โขรก พริก กระเทียม ถั่วฝักยาว ตำพอหยาบ จากนั้น ใส่ น้ำเชื่อม กะปิ และน้ำมะนาวลงไป ผสมให้ส่วนผสมน้ำยำละลาย จากนั้นใส่ขนมจีนลงไปคลุก\r\n-ตามด้วย กุ้งแห้ง ยอดชะอม และมะเขือเทศ คลุกให้ส่วนผสมเข้ากัน\r\n-เสริฟยำขนมจีน หรือ ตำขนมจีน อาหารอีสาน เมนูยำ กับข้าวง่ายๆ', '-ขนมจีน 3 จับ\r\n-กะปิ 1 ช้อนโต้ะ\r\n-น้ำเชื่อม 2 ช้อนโต้ะ\r\n-น้ำมะนาว 2 ช้อนโต้ะ\r\n-พริกสดบด 2 ช้อนโต้ะ\r\n-กระเทียมบด 1 ช้อนชา\r\n-มะเขือเทศหั่น 2 ลูก\r\n-ยอดชะอม 5-6 ยอด\r\n-กุ้งแห้ง 2 ช้อนโต้ะ\r\n-ถั่วฝักยาวซอย หยาบๆ 3 ช้อนโต้ะ', 'f007'),
('f008', 'ลาบไก่เส้นบุก', 'C06', 'r3', '-ลวก เนื้ออกไก่ และเส้นบุกให้สุก จากนั้นพักไว้ก่อน\r\n-นำเนื้ออกไก่ลวก เส้นบุกลวก น้ำมะนาว น้ำตาล น้ำปลา ข้าวคั่ว พริกป่น ใบสะระแหน่ ต้นหอมซอย และหอมแดงซอย นำมาผสมให้ส่วนผสมเข้ากัน สามารถเสริฟรับประทานกับผักสด ข้าวสวยหรือข้าวเหนียวได้เลย', '-เส้นบุก ½ ถ้วย\r\n-เนื้ออกไก่สับ ½ ถ้วย\r\n-น้ำมะนาว 1 ช้อนโต้ะ\r\n-น้ำตาล 1 ช้อนชา\r\n-น้ำปลา 1 ช้อนโต้ะ\r\n-ข้าวคั่วบด 1 ช้อนชา\r\n-พริกป่น 1 ช้อนชา\r\n-ใบสะระแหน่ 5-10 ใบ\r\n-ต้นหอมซอย 1 ช้อนโต้ะ\r\n-หอมแดงซอย 1 ช้อนโต้ะ', 'f008'),
('f009', 'อกไก่ย่างตะไคร้', 'C09', 'r3', '-นำอกไก่ มาหมักกับตะไคร้ เกลือ น้ำตาล ซอสหอยนางรม และซอสปรุงรส หมักไว้สัก 30 นาที\r\n-จากนั้นนำไปย่าง ให้เหลืองสุก ก็นำมาหั่นจักใส่จานรับประทานได้ อาหารคลีน เมนูอกไก่ กับข้าวง่ายๆ สำหรับคนลดน้ำหนัก', '-อกไก่ 2 ชื้น\n-ตะไคร้ซอย 2 ช้อนโต้ะ\n-เกลือ 2 ช้อนชา\n-น้ำตาล 1 ช้อนโต้ะ\n-ซอสน้ำมันหอย 1 ช้อนโต้ะ\n-ซอสปรุงรส 1 ช้อนโต้ะ', 'f009'),
('f010', 'คอหมูแดดเดียวทอด', 'C03', 'r3', '-นำเนื้อคอหมูมาทุบเพื่อให้หมูนุ่มและเนื้อหมูดูดซึมน้ำหมักได้ดี\r\n-นำเนื้อคอหมูมาหมัก ด้วย น้ำตาล น้ำมันงา ซอสถั่วเหลือง ซอสปรุงรส พริกไทย และน้ำเปล่า นวดให้น้ำหมักซึมเข้าเนื้อ\r\n-ตั้งกระทะน้ำมันให้ร้อน นำเนื้อหมูแดดเดียวลงไปทอด เมื่อสุกก็นำมาเสริฟรับประทานได้', '-เนื้อคอหมู หั่นเป้นชิ้นพอคำ 1 ถ้วย\r\n-น้ำมันงา 2 ช้อนชา\r\n-ซอสน้ำมันหอย 2 ช้อนชา\r\n-ซอสถั่วเหลือง 2 ช้อนชา\r\n-ซอสปรุงรส 2 ช้อนชา\r\n-พริกไทยป่น 1 ช้อนชา\r\n-น้ำตาลปี๊บ 1 ช้อนโต้ะ\r\n-น้ำเปล่า 3 ช้อนโต้ะ\r\n-น้ำมันพืช 1 ถ้วย (ใช้สำหรับทอด)', 'f010'),
('f011', 'แหนมคลุก', 'C06', 'r3', '-นำข้าวหุงสุก มาผสมกับ พริกแกงเผ็ด น้ำปลา น้ำตาล และไข่ไก่ นวดให้ส่วนผสมเข้ากันและปั้นเป็นก้อนกลมๆ\r\n-เตรียมแป้งข้าวเจ้าผสมน้ำ ชุปที่ก้อนข้าว และนำลงไปทอดในน้ำมันร้อนๆ ให้เหลืองกรอบ สุก จากนั้นนำข้าวทอดมาพักให้เย็น\r\n-เตรียมทำแหนมคลุก โดย ผสม ข้าวทอด แหนมสด น้ำปลา น้ำมะนาว พริกป่น หนังหมู หอมแดง ต้นหอมและขิงซอย คลุกเคล้าให้ส่วนผสมเข้ากัน\r\n-เสริฟ แหนมคลุก โรยหน้าด้วย ถั่วลิสงคั่ว เสร็จแล้ว กับข้าว เมนูอาหารง่ายๆ ตามร้านอาหาร', '-ข้าวหุงสุก 1 ถ้วย\n-พริกแกงเผ็ด 2 ช้อนโต้ะ\n-น้ำปลา 2 ช้อนโต้ะ\n-น้ำตาล 1 ช้อนโต้ะ\n-แป้งข้าวเจ้า 2 ช้อนโต้ะผสมน้ำเปล่า 4 ช้อนโต้ะ\n-ไข่ไก่ 1 ฟอง\n-หนังหมูลวกสุกซอย 2 ช้อนโต้ะ\n-พริกป่น 1 ช้อนโต้ะ\n-แหนมสด 2-3 ชิ้น\n-น้ำมะนาว 2 ช้อนโต้ะ\n-น้ำปลา 2 ช้อนโต้ะ\n-ถั่วลิสงคั่ว 2 ช้อนโต้ะ\n-ขิงซอย 2 ช้อนโต้ะ\n-หอมแดงซอย 2 ช้อนโต้ะ\nต้นหอมซอย 1 ช้อนโต้ะ\nผักสดสำหรับทานกับแหนมคลุก', 'f011'),
('f012', 'ไส้กรอกอีสาน', 'C09', 'r3', '-ล้างไส้หมูให้สะอาด โดยขั้นตอนนี้สำคัญมาก ต้องล้างไส้หมูให้สะอาดทั้ง 2 ด้าน ด้านในและด้านนอก โดยมีเคล็ดลับโดยใช้แป้งมันมาทาที่ตัวไส้เพื่อให้ไส้จับตัวไขมัน เวลาล้างไขมันในไส้ถึงออกง่าย\r\n-เตรียมไส้ โดย เอาหมูสับ มันหมูสับ ตะไคร้บด เกลือ น้ำตาล พริกไทยขาว พริกไทยดำ ผสมให้ส่วนผสมเข้ากัน จากนั้นนำข้าวสวยมาคลุกกับหมูหมักพักไว้ 30 นาที\r\n-นำหมูหมักมายัดใส่ไส้หมูและเอาด้วยมามัดเป็นท่อนๆ จากนั้นนำไส้หมูมาผึ่งแดดอ่อนๆ 1 วัน เราจะได้ไส้กรอกที่หวานอมเปรี้ยว\r\n-นำไส้กรอกอีสานไปย่างไฟให้สุกด้านนอกเหลืองๆไม่ไหม้เกินไป เสริฟไส้กรอกอีสานทานคู่กัยเครื่องเคียง เช่น กะหล่ำปลี พริกสด', '-ไส้หมู 1 กิโลกรัม\r\n-หมูสับเนื้อส่วนสะโพก 1 กิโลกรัม\r\n-มันหมูสับ 1/2 กิโลกรัม\r\n-ตะไคร้บด 2 ช้อนโต้ะ\r\n-เกลือ 2 ช้อนโต้ะ\r\n-น้ำตาล 1 ช้อนโต้ะ\r\n-พริกไทยขาวบด 2 ช้อนโต้ะ\r\n-พริกไทยดำบด 2 ช้อนโต้ะ\r\n-ข้าวสวยสุก 1 ถ้วย', 'f012'),
('f013', 'แกงส้มปลาโจก', 'C04', 'r3', '-ล้างปลาให้สะอาด จากนั้นตั้งน้ำให้เดือด ใส่ตะไคร้ลงไป และใส่ปลาลงไปลวกให้สุก จากนั้นนำปลาขึ้นมาพักไว้ก่อน เทน้ำคาวปลาทิ้งไปเลย\r\n-ตั้งหม้อต้ม ใส่น้ำซุปลงไป ตามด้วย เห็ดฟาง ข่า ใบมะกรูด ปรุงรสด้วย น้ำปลา เกลือ น้ำมะขามเปียก น้ำตาล จากนั้นใส่ ปลา เห็ดฟางและมะเขือลงไปต้ม\r\n-เสริฟ แกงส้มปลาโจก บางที่เรียกว่า ต้มส้ม หรือ ต้มยำแบบอีสาน กับข้าวง่ายๆ อาหารไทย 4 ภาค อาหารอีสาน', '-ปลาโจก สับเป็นชิ้นพอคำ 1 ถ้วย\r\n-น้ำซุปหมู 2 ถ้วย\r\n-เห็ดฟาง 4-5 ชิ้น\r\n-ข่าหั่น 2-3 ชิ้น\r\n-ตะไคร้ 2 ต้น\r\n-ใบมะกรูด 4-5 ใบ\r\n-น้ำปลา 2 ช้อนโต้ะ\r\n-เกลือ 1 หยิบมือ\r\n-น้ำมะขามเปียก 1 ช้อนโต้ะ\r\n-มะเขือเทศ หั่นเป็นซี่ 2 ลูก\r\n-น้ำตาล 1 ช้อนชา', 'f013'),
('f014', 'แกงลาว', 'C04', 'r3', '-โขลก พริก หอมแดง ตะไคร้ ข้าวเหนียวและกิปิให้ละเอียดเข้ากัน\r\n-ต้มน้ำในหม้อ ใส่เครื่องแกงลงไปต้ม ตามด้วยน้ำปลา น้ำปลาร้า ชิมให้ได้รสชาติที่ต้องการ จากนั้นใส่ผักที่เหลือลงไปเลย โดยใส่ ผักที่สุกยากลงไปก่อน เช่น หน่อไม้ บวบ เห็ด\r\n-แล้วใส่ผักที่เหลือลงไปตามด้วยเนื้อปลาย่าง เสริฟแกงลาวพร้อมรับประทานได้', '-หน่อไม้สดลวกสุก สไลด์บางๆ 2 หน่อ\r\n-เนื้อปลาดุกย่าง 1 ตัว\r\n-ยอดชะอม 1 กำ\r\n-ใบแมงลัก 1 ถ้วย\r\n-ใบย่านาง 1 ถ้วย\r\n-น้ำปลาร้า 5 ช้อนโต้ะ\r\n-น้ำปลา 2 ช้อนโต้ะ\r\n-ฟักทอง หั่นเป็นชิ้นพอคำ 1 ถ้วย\r\n-เห็ดฟางหั่นครึ่ง 1 ถ้วย\r\n-ข้าวโพดอ่อนและบวบ\r\n-พริกสด 4-5 เม็ด\r\n-หอมแดง 2-3 หัว\r\n-ตะไคร้ซอย 2 ช้อนโต้ะ\r\n-ข้าวเหนียวแช่น้ำ 2 ช้อนโต้ะ\r\n-กะปิ 1 ช้อนโต้ะ', 'f014'),
('f015', 'ตับหวาน', 'C06', 'r3', '-นำตับหมูมาลวกในน้ำร้อนให้สุก จากนั้นนำใส่ชาม\r\n-นำ น้ำตาล น้ำปลา น้ำมะนาว พริกป่น ข้าวคั้ว ต้นหอม หอมแดง และใบสาระแหน่ ผสมให้เข้ากัน\r\n-เสริฟตับหวาน ทานกับข้าวเหนียว', '-ตับหมูหั่น 1 ถ้วย\r\n-ข้าวคั่ว 1 ช้อนโต๊ะ\r\n-พริกป่น 1 ช้อนโต๊ะ\r\n-น้ำมะนาว 2 ช้อนโต๊ะ\r\n-น้ำปลา 1 ช้อนโต๊ะ\r\n-น้ำตาล 1 ช้อนชา\r\n-ต้นหอมซอย 1 ช้อนโต๊ะ\r\n-ใบสาระแหน่ 2 ช้อนโต้ะ\r\n-หอมแดงซอย 3 ช้อนโต้ะ', 'f015'),
('f016', 'ไก่ย่าง', 'C09', 'r3', '-นำไก่ไปหมักกับ เกลือ น้ำตาล กระเทียม หอมแดง ขิง ตะไคร้ หมักประมาณ 15 นาที\r\n-นำไก่พร้อมเครื่องที่หมัก ไปอบ จนไก่สุกสีออกแดงเหลือง\r\n-เสริฟใส่จานพร้อมข้าวเหนียว กับข้าวง่ายๆ เมนูย่าง อาหารอีสาน', '-ไก่ 10 ชิ้น\r\n-เกลือ 2 ช้อนชา\r\n-น้ำตาล 3 ช้อนโต้ะ\r\n-กระเทียมบด 2 ช้อนโต้ะ\r\n-หอมแดงบด 2 ช้อนโต้ะ\r\n-ขิงบด 2 ช้อนโต้ะ\r\n-ตะไคร้บด 2 ช้อนโต้ะ\r\n-น้ำมันพืช 2 ช้อนโต้ะ', 'f016'),
('f017', 'ลาบปลาหมึก', 'C06', 'r3', '-ลวกปลาหมึกให้สุก จากนั้นนำมาพักใส่จากเตรียมอาหาร\r\n-นำปลาหมึกลวก คลุกกับ ข้าวคั่ว น้ำปลา น้ำตาล น้ำมะนาว พริกป่น หอมแดง ผักชีฝรั่ง และสาระแหน่ คลุกเคล้าให้เข้ากัน\r\n-เสริฟลาบปลาหมึก พร้อมรับประทาน', '-ปลาหมึกหั่นเป็นชิ้นพอคำ 1 ถ้วย\r\n-ข้าวคั่ว 1 ช้อนโต้ะ\r\n-น้ำปลา 1 ช้อนโต้ะ\r\n-น้ำตาล 1 ช้อนชา\r\n-น้ำมะนาว 1 ช้อนโต้ะ\r\n-พริกป่น 2 ช้อนชา\r\n-หอมแดงซอย 2 ช้อนโต้ะ\r\n-ผักชีฝรั่งซอย 1 ช้อนโต้ะ\r\n-ใบสาระแหน่ 2-3 ใบ', 'f017'),
('f018', 'ซุปหน่อไม้', 'C01', 'r3', '-ต้มหน่อไม้กับปลาร้า\r\n-ใส่ข้าวคั่ว น้ำปลา น้ำตาล พริก หอมแดง ผสมให้เข้ากัน ปิดไa และใส่น้ำมะนาวลงไปคลุกให้เข้ากัน\r\n-เสริฟใส่จาน ทานกับข้าวเหนียว', '-sน่อไม้ดอก ต้ม และ หั่นเป้นฝอย 1 ถ้วย\r\n-หอมแดงหั่น 2 ช้อนโต้ะ\r\n-น้ำปลาร้า 2 ช้อนโต้ะ\r\n-ข้าวคั่ว 2 ช้อนโต้ะ\r\n-น้ำปลา 1 ช้อนโต้ะ\r\n-น้ำตาล 1 ช้อนชา\r\n-พริกบด 1 ช้อนชา\r\n-ใบสาระแหน่ 2-3 ใบ\r\n-น้ำมะนาว 2 ช้อนโต้ะ', 'f018'),
('f019', 'ตำถั่วฝักยาว', 'C05', 'r3', '-นำครก โขรก กระเทียมและพริก จากนั้น ปรุงรสด้วย น้ำตาล น้ำปลา น้ำปลาร้า และน้ำมะนาว ผสมให้ส่วนผสมเข้ากัน\r\n-ใส่ถ้วยผักยาว และมะเขือเทศลงไป โขรกให้พอบุบแตก ไม่ต้องให้ละเอียดมาก ชิมรสตามใจชอบ เสริฟใส่จาน กับข้าวง่ายๆ เมนูยำ', '-พริกบด 2 ช้อนโต้ะ\r\n-น้ำตาลปี๊บ 1 ช้อนโต้ะ\r\n-น้ำปลา 1 ช้อนโต้ะ\r\n-น้ำมะนาว 1 ช้อนโต้ะ\r\n-มะเขือเทศหั่นชิ้น 2 ลูก\r\n-น้ำปลาร้า 1 ช้อนโต้ะ', 'f019'),
('f020', 'ยำมะม่วง', 'C06', 'r3', '-เตรียมชาม ใส่ น้ำตาล น้ำปลา มะนาว และ พริกผสมให้เข้ากัน\r\n-ใส่ มะม่วง ถั่วลิสง และหอมแดง คลุกให้เข้ากัน\r\n-เสริฟใส่จาน โรยหน้าด้วยผักชี เมนูยำ กับข้าวง่ายๆ อาหารอีสาน', '-มะม่วงสับเป็นเส้น 1 ผล\r\n-กุ้งแห้ง 1 ช้อนชา\r\n-พริกบด 1 ช้อนชา\r\n-น้ำมะนาว 1 ช้อนชา\r\n-น้ำปลา 3 ช้อนโต้ะ\r\n-น้ำตาล 1 ช้อนชา\r\n-ผักชีหั่น 1 ช้อนชา\r\n-หอมแดงหั่น 1 ช้อนชา\r\n-ถั่วลิสง 1ช้อนชา', 'f020'),
('f021', 'ตำถั่วหมูกรอบ', 'C05', 'r3', '-เตรียมครก ใส่ กระเทียม พริกขี้หนู น้ำตาลปี๊บ น้ำปลา และ น้ำมะนาว คลุกเคล้าให้เข้ากัน\r\n-ใส่ถั่วลงไปตำในครก พอให้บุบ (ไม่ต้องตำจนละเอียด) และใส่มะเขือเทศหั่นลงไปโคลก\r\n-เสร็ฟใส่จาน โรยหมูกรอบบนจาน และแต่งหน้าจานด้วยหมูกรอบ', '-ถั่วฝักยาวหั่นเป็นท่อน 1 ถ้วย\r\n-กุ้งแห้ง (แช่น้ำให้นิ่ม) 2 ช้อนโต้ะ\r\n-กระเทียมบด 1 ช้อนชา\r\n-พริกขี้หนูบด 1 ช้อนโต้ะ\r\n-มะม่วงน้ำดอกไม้สับเป็นฝอย 1 ถ้วย\r\n-น้ำตาลปี๊บ 1 ช้อนโต้ะ\r\n-น้ำปลา 1 ช้อนโต้ะ\r\n-น้ำมะนาว 1 ช้อนโต้ะ\r\n-ไข่ต้มหั่นเป็นชิ้น 1 ฟอง\r\n-หมูกรอบหั่นเป็นชิ้น 3 ช้อนโต้ะ\r\n-มะเขือเทศหั่นชิ้น 1 ลูก\r\n', 'f021'),
('f022', 'ส้มตำเต้าหู้กรอบ', 'C05', 'r3', '-เต้าหู้แผ่นหั่นเป็นท่อนสี่เหลี่ยมพอคำ 6 ก้อน\r\n-มะละกอสับเป็นเส้น 1 ถ้วย\r\n-แดรอทสับเป็นเส้น ครึ่ง ถ้วย\r\n-ถั่วฝักยาวหั่นเป็นท่อนพอคำ 3 ช้อนโต้ะ\r\n-มะเขือเทศหั่นเป็นชิัน 1 ลูก\r\n-กระเทียมบด 1 ช้อนชา\r\n-พริกบด 1 ช้อนโต้ะ\r\n-น้ำปลา 1 ช้อนโต้ะ\r\n-น้ำตาลปี๊บ 1 ช้อนโต้ะ\r\n-น้ำมะนาว 1 ช้อนโต้ะ\r\n-กุ้งแห้ง 1 ช้อนโต้ะ\r\n-ถั่วลิสงทิด 2 ช้อนโต้ะ\r\n-น้ำมันพืชสำหรับทอด', '-ตั้งกระทะน้ำมันให้ร้อน นำเต้าหู้ลงไปทอดให้ กรอบ และพักให้สะเด็ดน้ำมันไว้\r\n-เตรียมครก นำกระเทียมลงไปโครกกับ พริกบด ถั่วฝักยาว กุ้งแห้งและถั่วลิสง\r\n-ใส่ น้ำตาล น้ำปลา และน้ำมะนาวลงไป คลุกเคล้าให้เข้ากัน จากนั้นใส่ มะเขือเทศ เส้นมะละกอ และ เส้นแครอทลงไปคลุกเคล้าให้เข้ากัน\r\n-เสริฟใส่จาน โรยหน้าด้วยเต้าหู้ทอด', 'f022'),
('f023', 'ผัดหมี่โคราช', 'C02', 'r3', '-ตั้งกระทะน้ำมัน นำกระเทียม หอมแดง และหมูสับลงไปผัดให้หอม จากนั้นใส่เส้นหมี่โคราชลงไป\r\n-ใส่น้ำปลา น้ำตาล ซีอิ้วตำ น้ำมะขามเปียก เต้าเจี้ยว และน้ำเปล่าลงไป ผัดให้ส่วนผสมเข้ากันกับเส้นและเมื่อเส้นเริ่มนุ่มแล้ว\r\n-ใส่ถั่วงอกและใบหอมลงไปผัดกับเส้นหมี่ในกระทะ ผัดให้เข้ากัน\r\n-เสริฟใส่จานดรยหน้าด้วย พริกไทย ถั่งลิสงบดและพริกป่น', '-เส้นหมี่โคราช 1 ถ้วย\r\n-กระเทียมบด 1 ช้อนชา\r\n-หอมแดงซอย 1  ช้อนโต้ะ\r\n-หมูสับ 2 ช้อนโต้ะ\r\n-เต้าเจี้ยว 1 ช้อนโต้ะ\r\n-น้ำปลา 2 ช้อนโต้ะ\r\n-น้ำตาล 2 ช้อนโต้ะ\r\n-น้ำมะขามเปียก 1 ช้อนโต้ะ\r\n-ซีอิ้วดำ 1 ช้อนชา\r\n-พริกไทย 1 ช้อนชา\r\n-น้ำเปล่า 3 ช้อนโต้ะ\r\n-น้ำมันพืช 2 ช้อนโต้ะ\r\n-ถั่วงอกและใบหอม 1 ถ้วยตวง', 'f023'),
('f024', 'แกงหน่อไม้ใบย่านาง', 'C04', 'r3', '-โขรก พริกสด หอมแดง ตะไคร้ ข้าวเหนียว กะปิ และเกลือ ให้ละเอียดเข้ากัน เป็นเครื่องพริกแกง\r\n-ต้มน้ำให้เดือด จากนั้นใส่เครื่องแกงลงไปต้ม ตามด้วย เห็ด ฟักทอง หน่อไม้และบวบ\r\n-ใส่ปลาร้า และน้ำปลา ปรุงรสตามใจชอบ\r\n-ใส่ ยอดชะอม ใบแมงลัก เนื้อปลาดุก และใบย่านางขั้นตอนสุดท้าย', 'หน่อไม้สดหั่นบางๆ 1 ถ้วย\r\nเนื้อปลาดุกย่าง 1 ถ้วย\r\n-ยอดชะอม 1 ถ้วย\r\n-ใบแมงลัก 1/2 ถ้วย\r\n-ใบย่านาง 1/2 ถ้วย\r\n-น้ำปลาร้า 4 ช้อนโต้ะ\r\n-น้ำปลา 2 ช้อนโต้ะ\r\n-ฟักทองหั้นเป็นชิ้นพอคำ 1/2 ถ้วย\r\n-เห็ดฟาง 1 ถ้วย\r\n-บวบหั้นเป็นชิ้นพอคำ 1/2 ถ้วย\r\n-พริกสด 4 ช้อนโต้ะ\r\n-หอมแดง 3 หัว\r\n-ตะไคร้ 1 ต้น\r\n-ข้าวเหนียวแช่น้ำ 1 คืน 1 ช้อนโต้ะ\r\n-กะปิ 1 ช้อนโต้ะ\r\n-เกลือ 1 ช้อนโต้ะ', 'f024'),
('f025', 'น้ำพริกปลาร้าสับ', 'C05', 'r3', '-ลวกปราร้าให้พอสุก พักไว้ก่อน\n-โขลก พริกขี้หนู หอมแดง กระเทียม ขิง กระชาย และตะไคร้ ให้ละเอียดเข้ากัน\n-ใส่ปลาร้าและน้ำมะขามเปียกลงไปโขลกด้วยให้เข้ากัน\n-เสริฟใส่ถ้วยน้ำพริก โรยหน้าด้วยใบมะกรูด', '-ปลาร้า 2 ตัว\r\n-พริกขี้หนู 5 เม็ด\r\n-หอมแดง 3 หัว\r\n-กระเทียม 3 หัว\r\n-ใบมะกรูดซอย 1 ช้อนโต้ะ\r\n-ขิงซอย 1 ช้อนโต้ะ\r\n-กระชายซอย 1 ช้อนโต้ะ\r\n-ตะไคร้ซอย 1 ช้อนโต้ะ\r\n-น้ำมะขามเปียก 1 ช้อนโต้ะ', 'f025'),
('f026', 'ต้มแซบหมู', 'C01', 'r3', '-ต้มข่ากับตะไคร้ 10 นาที\r\n-ใส่หมูและเครืองใน ต้มจนสุข\r\n-ใส่ มะเขือ ใบมะกรูด น้ำปลา พริก และ รสดี ต้มอีกสักพักแล้วใส่น้ำมะนาว\r\n-เสริฟ ใส่ชาม ทานกับข้าวเหนียวไก่ย่าง', '-Pork slice 1 cup\r\n-Pig lung slice 1 cup\r\n-Galangal slice 3 piece\r\n-lemon grass slice 3 piece\r\n-tometo slice 1 tometo\r\n-lemon juice 2 tablespoon\r\n-chilli 1 tablespoon\r\n-Kaffir lime leaves 2 piece\r\n-fish sauce 1 tablespoon\r\n-stock powder 1 teaspoon', 'f026'),
('f027', 'ลาบหมู', 'C06', 'r3', '-ต้มหมูสับ หนังหมูและตับหมูให้สุก จากนั้น นำเนื้อหมูสับที่ หนังหมูและตับหมูที่ต้มสุกแล้ว พักใส่ชาม\r\n-ปรุงรสลาบด้วย น้ำปลา น้ำตาล มะนาว ข้าวคั่ว ใบหอม ผักชีฝรั่ง พริกแห้งบด ใบสาระแหน่ และ หอมแดงซอย ผสมกับหมูสับ หนังหมูและตับหมูให้เข้ากัน\r\n-เสริฟใช่จาน ทานกับข้าวเหนียวและผัก เช่น แตงกวา ผักกากขาว และถั่วฝักยาว', '-หมูสับ 1 ถ้วย\r\n-หนังหมูหั่น 3 ช้อนโต้ะ\r\n-ตับหมูหั่น 3 ช้อนโต้ะ\r\n-ใบสาระแหน่ 1 ช้อนโต๊ะ\r\n-น้ำมะนาว 2 ช้อนโต๊ะ\r\n-น้ำปลา 2 ช้อนโต๊ะ\r\n-ข้าวคั่ว 2 ช้อนโต๊ะ\r\n-ใบหอมซอย 1 ช้อนโต๊ะ\r\n-ผักชีฝรั่งซอย 1 ช้อนโต๊ะ\r\n-พริกแห้งบด 1 ช้อนโต๊ะ\r\n-น้ำตาล 1 ช้อนชา\r\n-หอมแดงซอย 3 ช้อนโต้ะ\r\n', 'f027'),
('f028', 'ไข่เจียวใส่ไข่มดแดง', 'C03', 'r3', '-ตอกไข่ใส่ชาม ผสมกับ ไข่มดแดง ใบหอม มะเขือ น้ำปลา และ น้ำมันหอย ผสมให้เข้ากัน\r\n-ตั้งกระทะน้ำมันให้ร้อน ใช้ไฟอ่อน ใส่ไข่ลงไปทอด\r\n-ทอดให้เหลืองทั้งสองด้าน\r\n-เสริฟใส่จาน ทานกับ ซอสพริก และพริกน้ำปลา', '-ไข่ไก่ 3 ฟอง\r\n-ไข่มดแดง 1 ถ้วย\r\n-ใบหอมหั่น 1 ช้อนโต้ะ\r\n-มะเขือเทศหั่น 1 ผล\r\n-น้ำปลา 1 ช้อนโต้ะ\r\n-มันหอย 1 ช้อนโต้ะ\r\n-น้ำมันพืช 4 ช้อนชา\r\n-ซอสพริก 1 ถ้วยน้ำจิ้ม', 'f028'),
('f029', 'หมกปลาซิว', 'C06', 'r3', '-นำปลาซิวมาหมักด้วย ข้าวเบือ น้ำปลา เกลือ พริก ตะไคร้ ผักชี ต้นหอม และใบแมงลัก หมักประมาณ 15 นาที\r\n-นำปลาที่หมกมาห่อใบตอง หรือใบบัว ก็ได้ จากนั้นนำไปนึ่ง ประมาณ 30 นาทีให้ปลาสุก', '-ปลาซิว(ลูกปลา) 1 ถ้วย\r\n-ผักชีสับหยาบ 1 ช้อนโต้ะ\r\n-ใบต้นหอมหั่นหยาบ 2 ช้อนดต้ะ\r\n-พริกสดโขรกหยาบ 1 ช้อนโต้ะ\r\n-น้ำปลา  1 ช้อนโต้ะ\r\n-ตะไคร้โขรกละเอียด 1 ช้อนโต้ะ\r\n-ใบแมงลัก 1 / 2 ถ้วย\r\n-เกลือ 1 ช้อนชา\r\n-ข้าวเบือ 1 ช้อนชา', 'f029'),
('f030', 'แกงผักหวานใส่ไข่มดแดง', 'C04', 'r3', '-ต้มน้ำให้เดือด จากนั้น ใส่พริก ตะไคร้ ใบมะกรูด หอมแดง และเกลือ ลงไป จากนั้นปรุงรสด้วย น้ำปลา และน้ำปลาร้า\r\n-ใส่ผักหวาน และใบแมงลักลงไป และใส่ไขมดแดงลงไป ต้มอีกนิดหน่อย และเสริฟใส่ชาม', '-ผักหวานป่า ประมาณ 1 ถ้วย\r\n-ไข่มดแดง 1 ถ้วย\r\n-ใบแมงลัก 1 ถ้วย\r\n-น้ำปลาร้า 2 ช้อนโต๊ะ\r\n-นํ้าปลา 1 ช้อนโต๊ะ\r\n-น้ำเปล่า 1 ถ้วย\r\n-พริกสดโขรกหยาบ 2 ช้อนโต้ะ\r\n-ตะไคร้หั่นหยาบ 5 ท่อน\r\n-หอมแดงโขรกหยาบ 3 ช้อนโต้ะ\r\n-เกลือ ½ ช้อนชา\r\n-ใบมะกรูดฉีก 3 ใบ', 'f030'),
('f031', 'แกงเห็ดฟางใส่ผักหวาน', 'C04', 'r3', '-ต้มน้ำให้เดือด จากนั้นใส่ เห็ด หอมแดง ฟักทอง และตะไคร้ ลงไป\r\n-ปรุงรสด้วย น้ำปลา น้ำปลาร้า และพริกสด\r\n-ใส่ใบแมงลักและผัดหวานลงไปต้มต่อและเสริฟใส่ชาม', '-ยอดผักหวาน 1/2 ถ้วย\r\n-เห็ดนางฟ้า 1/2 ถ้วย\r\n-เห็ดฟาง 1/2 ถ้วย\r\n-เห็ดฟางดอกตูม 1/2 ถ้วย\r\n-เห็ดนางฟ้า เห็ดเข็มทอง เห็ดหูหนู ผสมกัน ½ ถ้วย\r\n-พริกสดโขรก 2 ช้อนโต้ะ\r\n-หอมแดงโขรกหยาบ 3 ช้อนโต้ะ\r\n-ตะไคร้หั่นหยาบ 4-5 ท่อน\r\n-ฟักทองหั่นเป็นชิ้นพบคำ ครึ่ง ถ้วย\r\n-ใบแมงลัก 1 กำมือ\r\n-น้ำปลาร้า 1 ช้อนโต้ะ\r\n-น้ำปลา 1 ช้อนโต้ะ', 'f031'),
('f032', 'ไก่นึ่งสูตรอีสาน', 'c13', 'r3', '-โขรก หอมแดง กระเทียม รากผักชี ข่า ตะไคร้ ให้ละเอียดเข้ากัน จากนั้น ผสมกับ ใบมะกรูดฉีก เกลือและน้ำตาล\r\n-นำส่วนผสมทั้งหมดมาหมักไก่ สัก 30 นาที\r\n-นำไก่ที่หมักลงไปนึ่ง 30-45 นาที สังเกตุให้ไก่สุก เสริฟใส่จาน', '-ไก่ ครึ่งตัว\r\n-หอมแดง 4 หัว\r\n-กระเทียม 4-5 กลีบ\r\n-รากผักชี 2 ต้น\r\n-ข่าหั่นแว่น 3-4 ชิ้น\r\n-ตะไคร้ หั่นหยาบ 1 ต้น\r\n-เกลือ 1 ช้อนชา\r\n-น้ำตาล ครึ่ง ช้อนชา\r\n-ใบมะกรูด 2 ใบ', 'f032'),
('f033', 'ปากเป็ดทอด', 'C03', 'r3', '-ล้างปากเป็ดให้สะอาด โดยล้างด้วยโซดา รอบแรกและน้ำเปล่าอีกหลายๆรอบ จำเป็นต้องล้างให้สะอาดไม่อย่างนั้นอาหารจะมีรสคาว\r\n-นำปากเป็ดมาหมักกับส่วนผสม ประกอบด้วย รากผักชี กระเทียม พริกไทย น้ำตาล ซอสหอยนางรม ซอสปรุงรส หมักทิ้งไว้ในตู้เย็น 30 นาที\r\n-ตั้งกระทะน้ำมัน ใช้ไฟปานกลาง นำปากเป็ดลงไปทอดประมาณ 15 นาที ก็จะได้ปากเป็ดทอดที่ยังชุ่มน้ำหมักรสชาติเด็ด\r\n-เสริฟปากเป็ดทอด ทานคู่กับน้ำจิ้มแจ่ว', '-ปากเป็ด 5-10 ชิ้น (ฝ่าครึ่งให้พิคำรับประทาน)\r\n-ซอสหอยนางรม 2 ช้อนโต้ะ\r\n-ซอสปรุงรส 2 ช้อนโต้ะ\r\n-น้ำตาล 2 ช้อนโต๊ะ\r\n-กระเทียมบด 3 ช้อนโต้ะ\r\n-พริกไทยบด 2 ช้อนโต้ะ\r\n-รากผักชีบด 2 ช้อนโต้ะ\r\n-น้ำมันสำหรับทอด\r\n-น้ำจิ้มแจ่ว', 'f033'),
('f034', 'ยำถั่วพลู', 'C06', 'r2', '\r\n	เตรียมเนื้อสัตว์ โดยการ ลวกหมูสับ และลวกกุ้ง จากนั้นพักเอาไว้ก่อน\r\n	เตรียมน้ำยำ โดย ผสม น้ำตาล น้ำปลา น้ำมะนาว กะทิ และน้ำพริกเผา ผสมให้ส่วนผสมเข้ากัน\r\n	จากนั้นใส่ ถั่วพลู หอมแดง กุ้งลวก หมูสับ ถั่วเขียวบด และถั่วลิสงบด ผสมให้ส่วนผสมเข้ากับน้ำยำ\r\n	เสริฟใส่จาน โรยหน้าด้วย ไข่ต้ม หอมเจียว และพริกทอด\r\n', '	หมูสับ 3 ช้อนโต้ะ\r\n	กุ้งสด 5-6 ตัว\r\n	หอมเจียว 2 ช้อนโต้ะ\r\n	หอมแดงซอย 2 ช้อนโต้ะ\r\n	พริกแห้งทอด 4-5 เม็ด\r\n	ถั่วลิสงคั่วโขรกหยาบๆ 2 ช้อนโต้ะ\r\n	ถั่วเขียวนึ่งบด 2 ช้อนโต้ะ\r\n	ถั่วพูซอย 1 จาน\r\n	น้ำมะนาว 2 ช้อนโต้ะ\r\n	น้ำปลา 2 ช้อนโต้ะ\r\n	น้ำตาลปี๊บ 1 ช้อนโต้ะ\r\n	น้ำพริกเผา 1 ช้อนโต้ะ\r\n	กะทิ 2 ช้อนโต้ะ\r\n	ไข่ต้ม 2 ฟอง \r\n', 'f034'),
('f035', 'หมูย่างดอกดาหลา', 'C09', 'r2', '1.	นำเนื้อหมูมาหมัก กับ รากผักชี กระเทียม พริกไทย ปรุงรสด้วย ซอสปรุงรส และน้ำมันหอย จานนั้นนำไปย่าง ให้สุก จากนั้นนำมาหั่นเป็นชิ้นในจานรอน้ำยำ\r\n2.	เตรียมเครื่องเคียง โดย นำตะลิงปลิง และดอกดาหลา มาแช่ในน้ำเย็นก่อน ให้ดูสดๆ\r\n3.	เตรียมน้ำยำ โดย ใส่ น้ำพริกเผา น้ำตาล น้ำปลา น้ำมะนาว นมจืด และพริกสด มาคลุกให้ส่วนผสมละลายเข้ากัน\r\n4.	จากนั้นใส่ตะลิงปลิง และดอกดาหลาลงไปคลุกกับน้ำยำ\r\n5.	จัดจาน โดย วางไข่ต้มยางมะตูม หมูยาง และน้ำยำที่ผสมดอกดาหลาและตะลิงปลิงบนจาน โรยหน้าด้วย หอมเจียว เสร็จ อาหารยำ \r\n', '	เนื้อหมูสันใน 1 ขีด\r\n	รากผักชีบดหยาบ 1 ช้อนโต้ะ\r\n	กระเทียมบด 1 ช้อนโต้ะ\r\n	พริกไทยเม็ดบด 1 ช้อนโต้ะ\r\n	ซอสปรุงรส 2 ช้อนโต้ะ\r\n	ซอสน้ำมันหอย  1 ช้อนโต้ะ\r\n	ดอกดาหลา เอาส่วนกลีบดอก ซอยเป็นเส้น 1 จาน\r\n	ตะลิงปลิง ซอยหยาบๆ 3 ผล\r\n	น้ำตาลปี๊บ 2 ช้อนโต้ะ\r\n	น้ำปลา 2 ช้อนโต้ะ\r\n	น้ำมะนาว 1 ช้อนโต้ะ\r\n	นมข้นจืด 2 ช้อนโต้ะ\r\n	น้ำพริกเผา 1 ช้อนโต้ะ\r\n	พริกสดโขรกหยาบ 2 ช้อนโต้ะ\r\n	หอมเจียว 2 ช้อนโต้ะ \r\n	ไข่ต้มยางมะตูม 2 ลูก\r\n', 'f035'),
('f036', 'ยำหูหมูแก้ว', 'C06', 'r2', '1.	ให้เตรีมภาชนะสำหรับยำ ใส่ น้ำเชื่อม น้ำปลา น้ำมะนาว พริก ผสมให้ส่วนผสมเข้ากัน\r\n2.	ใส่ หอมใหญ่ มะเขือเทศ และต้นหอมลงไปคลุก\r\n3.	ใส่หูหมูแก้วลงไปคลุก\r\n4.	เตรียมจานโดยจัดผักให้สวยงาม ราดนำหูหมูแก้วใส่จาน พร้อมรับประทาน\r\n', '	หูหมูแก้ว หั่นเป็นชิ้นพอคำ 1 จาน\r\n	น้ำเชื่อม 2 ช้อนโต้ะ\r\n	น้ำปลา 2 ช้อนโต้ะ\r\n	น้ำมะนาว 2 ช้อนโต้ะ\r\n	พริกสดบด 1 ช้อนโต้ะ\r\n	หอมใหญ่ซอย 2 ช้อนโต้ะ\r\n	มะเขือเทศหั่น 2 ช้อนโต้ะ\r\n	ใบหอมซอยหยาบๆ 2 ช้อนโต้ะ\r\n	ผักสำหรับแต่งหน้า เช่น ผักกาดหอม แตงกวา เป็นต้น\r\n', 'f036'),
('f037', 'ปลาดุกฟูคั่วกลิ้ง', 'C02', 'r2', '1.	ล้างปลาดุกให้สะอาด จากนั้นนำปลาดุกไปต้มให้เปลื่อย จากนั้นแกะเอาเฉพาะเนื้อปลาออกมา\r\n2.	จากนั้นนำเนื้อปลาไปบดให้ละเอียด จากานั้นนำเนื้อปลาดุกไปผสม เกล็ดขนมปัง และน้ำมันพืช ในอัตราส่วน เนื้อปลา 3 ส่วน เกล็ดขนมปัง 1 ส่วน และน้ำมันพืช 1 ส่วน ผสมให้ส่วนผสมเข้ากัน\r\n3.	จากนั้นนำปลาดุกลงไปทอดให้ฟู จะได้ปลาดุกฟู จากนั้นให้พักปลาดุกฟูเอาไว้ก่อน\r\n4.	เตรียมน้ำพริกคั่วกลิ้ง โดย โขรก หอมแดง กระเทียม ขิง ข่า ตะไคร้ พริกแห้ง พริกสด กะปิ เกลือ โขรกให้ส่วนผสมละเอียดเป็นเนื้อเดียวกัน เราได้พริกแกงคั่วกลิ้งแล้ว\r\n5.	ตั้งกระทะน้ำมัน ใส่น้ำมัน 1 ถ้วย ลงไป ผัดกับพริกแกงคั่วกลิ้ง ผัดให้แตกมันและพริกแกงสุก จากนั้นปรุงรส ด้วย น้ำตาลทั้ง 3 ชนิด ซอสปรุงรส น้ำปลา และน้ำมันหอย ผัดให้ส่วนผสมเข้ากัน ใส่ใบมะกรูดซอยลงไป พักให้เย็น\r\n6.	นำปลาดุกฟูที่ทอดไว้แล้วลงไปคลุกกับน้ำพริกคั่วกลิ้งคลุกให้แห้ง และโรยด้วยใบมะกรูดทอด\r\n7.	เสร็จสำหรับเมนูปลาดุกฟูคั่วกลิ้งแสนอร่อย\r\n', '	ปลาดุก 4-5 ตัว\r\n	เกล็ดขนมปัง 1 ถ้วย\r\n	น้ำมันพืช 1 ขวด\r\n	หอมแดงซอย 2 ช้อนโต้ะ\r\n	กระเทียมบด 2 ช้อนโต้ะ\r\n	ข่าซอย 2 ช้อนโต้ะ\r\n	ผิวมะกรูด 2 ช้อนโต้ะ\r\n	พริกไทย 2 ช้อนโต้ะ\r\n	ขมิ้นซอย 2 ช้อนโต้ะ\r\n	ตะไคร้ซอย 2 ช้อนโต้ะ\r\n	เกลือ 1 ช้อนชา\r\n	พริกขี้หนูสวน 2 ช้อนโต้ะ\r\n	พริกแห้ง 2 ช้อนโต้ะ\r\n	กะปิ 1 ช้อนโต้ะ\r\n	น้ำตาลทรายแดง 1 ช้อนโต้ะ\r\n	น้ำตาลทราย 1 ช้อนโต้ะ\r\n	น้ำตาลปี๊บ 1 ช้อนโต้ะ\r\n	ซอสปรุงรส 2 ช้อนโต้ะ\r\n	น้ำมันหอย 2 ช้อนโต้ะ\r\n	น้ำปลา 2 ช้อนโต้ะ\r\n	ใบมะกรูดซอย 1 ถ้วย\r\n	ใบมะกรูดทอด 1 ถ้วย\r\n\r\n\r\n', 'f037'),
('f038', 'มะระอบไอน้ำ', 'C13', 'r2', '1.	นำมะระมาควานไส้ขาวๆออกให้หมด ล้างน้ำให้สะอาด จากนั้นนำไปต้มในน้ำเกลือ 15 นาที และนำไปแช่ในน้ำเย็น และพักใส่จานเอาไว้ก่อน\r\n2.	เตรียมไส้ นำหมูสับและมันหมูบดมาผสมให้ส่วนผสมเข้ากัน ปรุงรสด้วย ซอสหอยนางรม ซอสปรุงรส และซีอิ้วขาว อย่างละ 3 ช้อนโต้ะ ตามด้วยวุ้นเส้น เห็ดหอม รากผักชีและพริกไทย นวดให้ให้ส่วนผสมเข้ากัน นำไปแช่เย็นในตู้เย็น 30 นาที\r\n3.	จากนั้นนำหมูบดมายัดไส้มะระที่ต้มเอาไว้แล้ว\r\n4.	ราดซอสหอยนางรม ซอสปรุงรส และซีอิ้วขาว อย่างละ 2 ช้อนโต้ะ บนจานมะระที่ยัดไส้ไว้แล้ว\r\n5.	นำไปนึ่งเป็นเวลา 2 ชั่วโมง ก็สามารถรับประทานได้\r\n', '	มะระหั่นท่อน 3-4 ท่อน\r\n	เนื้อหมูบด 1 กิโลกรัม\r\n	มันหมูบด 1 กิโลกรัม\r\n	ซอสน้ำมันหอย 5 ช้อนโต้ะ\r\n	ซอสปรุงรส 5 ช้อนโต้ะ\r\n	ซีอิ้วขาว 5 ช้อนโต้ะ\r\n	พริกไทย 3 ช้อนโต้ะ\r\n	รากผักชีบด 3 ช้อนโต้ะ\r\n	เห็ดหอมหั่น 5 ช้อนโต้ะ\r\n	วุ้นเส้นหั่น 10 ช้อนโต้ะ\r\n	เกลือ 1/2 กิโลกรัม\r\n', 'f038'),
('f039', 'ผัดมะระปลาเค็ม', 'C02', 'r2', '1.	ตั้งกระทะน้ำมันให้ร้อนนำเนื้อปลาเค็มมาทอดให้กรอบ เหลือง จากนั้นำปลาเค็มมาพักไว้ก่อน\r\n2.	ตั้งกระทะให้ร้อน นำกระเทียมมาผัดให้เหลือง จากนั้นใส่มะระลงไปผัด ตามด้วยปลาเค็ม\r\n3.	ปรุงรสด้วนซอสหอยนางรม และพริก ผัดให้ส่วนผสมเข้ากัน\r\n4.	เสริฟใส่จานพร้อมรับประทาน\r\n', '	เนื้อปลาอินทรีย์เค็ม หั่นเป็นชิ้นลูกเต๋า 5 ช้อนโต้ะ\r\n	มะระจีน ซอยเป็นชิ้นพอคำ 1 จาน\r\n	ซอสน้ำมันหอย 2 ช้อนโต้ะ\r\n	กระเทียมบด 2 ช้อนโต้ะ\r\n	พริกสดซอยเฉลียง 2 ช้อนโต้ะ\r\n	น้ำซุป 5 ช้อนโต้ะ\r\n	เกลือ 3 ช้อนโต้ะ\r\n	น้ำมันพืชสำหรับทอดและผัด\r\n', 'f039'),
('f040', 'สะเดาทรงเครื่อง\r\n', 'C06', 'r2', '\r\n1.	นำปลาดุกไปย่างให้สุก จากนั้นแกะเอาเฉพาะเนื้อปลาดุก นำมายีๆ เนื้อ\r\n2.	จากนั้นนำเนื้อปลาดุกมาคุลกกับเกร็ดขนมปัง นำเนื้อปลาดุกคลุกกับเกร็ดขนมปังไปทอดให้เหลืองกรอบฟู จะได้ปลาดุกฟู จากนั้นนำมาพักให้สะเด็ดน้ำมันไว้ก่อน\r\n3.	ตั้งน้ำต้ม ใส่เกลือลงไป รอให้น้ำเดือด จากนั้นนำไบสะเดาและยอดสะเดาลงไปลวก ใช้เวลาประมาณ 1 นาที จากนั้นนำมาแช่ในน้ำเย็น\r\n4.	ทำน้ำยำโดย ตั้งหม้อต้ม ใส่น้ำเปล่าลงไป 10 ช้อนโต้ะ ใส่น้ำตาล น้ำปลา และน้ำมะขามเปียกลงไป เคี่ยวให้น้ำยำเหนียว จากนั้นพักเอาไว้ก่อน\r\n5.	นำผักสะเดาลวกจัดใส่จาน วางเนื้อปลาดุกฟูด้านบน โรยหน้าด้วย ถั่วลิสงคั่ว หอมเจียว พริกทอด และราดด้วยน้ำยำ เสร็จพร้อมรับประทาน กับข้าวง่าย สะเดาทรงเครื่อง\r\n', '	ปลาดุก 1 ตัว\r\n	ใบสะเดาและยอดสะเดา 1 ถ้วย\r\n	เกร็ดขนมปัง 4 ช้อนโต้ะ\r\n	น้ำมันสำหรับทอด\r\n	น้ำตาลปี๊บ 5 ช้อนโต้ะ\r\n	น้ำปลา 3 ช้อนโต้ะ\r\n	น้ำมะขามเปียก 5 ช้อนโต้ะ\r\n	น้ำเปล่า 10 ช้อนโต้ะ\r\n	ถั่วลิสงคั่ว 2 ช้อนโต้ะ\r\n	หอมเจียว 2 ช้อนโต้ะ \r\n	พริกทอด 2 ช้อนโต้ะ\r\n', 'f040'),
('f041', 'ปลาช่อนเกยตื้น', 'C03', 'r2', '1.	ล้างปลาช่อนให้สะอาด จากนั้นให้บั้งเนื้อปลาให้ทั่ว ความกว้าง พอคำ จากนั้น ตั้งกระทะน้ำมันให้ร้อน จากนั้นทอดปลาให้เหลืองกรอบ จากนั้นนำมาพักให้สะเด็ดน้ำมัน\r\n2.	ตั้งหม้อต้ม นำมะเขือยาวที่หั่นท่อนมาต้มให้สุก ใช้เวลาประมาณ 3 นาที จากนั้นนำมาน็อกน้ำเย็น และพักไว้ก่อน\r\n3.	ตั้งกระทะน้ำมัน นำมะเขือยาวมาผัด กับเต้าเจี้ยว น้ำตาล และใบโหระพา ให้สุก\r\n4.	จัดจาน โดย วางปลาช่อนทอดบนจาน ราดมะเขือยาวผัดไว้ด้านข้างตัวปลาทอด ทานคู่กับน้ำจิ้มซีฟู๊ด และข้าวสวยร้อนๆ เสร็จ เมนูแสนง่าย แบบพื้นเมืองภาคกลาง\r\n', '	ปลาช่อน (ขนาด 9 ขีด) 1 ตัว\r\n	มะเขือยาวหั่นท่อนพอคำ 1 ถ้วย\r\n	เต้าเจี้ยว 2 ช้อนโต้ะ\r\n	น้ำตาลทราย 2 ช้อนโต้ะ\r\n	น้ำมันสำหรับทอด\r\n	ใบโหระพา 1 กำมือ\r\n	น้ำจิ้มซีฟู๊ด 1 ถ้วยน้ำจิ้ม ', 'f041'),
('f042', 'ปลาช่อนจู่ขิง', 'C02', 'r2', '1.	ตั้งกระทะทอดเนื้อปลาช่อนให้เหลืองสุก จากนั้นนำมาพักไว้ก่อน\r\n2.	ตั้งกระทะน้ำมัน ให้ไปอ่อน ใส่กระเทียม และขิงลงไปผัด จากนั้นปรุงรสด้วย พริกไทยและเต้าเจี้ยว\r\n3.	นำเนื้อปลาทอดลงไปผัดให้เข้ากับส่วนผสม จากนั้นเร่งไฟให้แรงจัด จากนั้นเทน้ำซุปลงไปผัด เติมต้นหอมลงไปผัด\r\n4.	เสริฟใส่จาน พร้อมรับประทาน อาหารง่ายๆ พร้อมสุขภาพดีด้วยขิง\r\n', '1.	เนื้อปลาช่อน ขนาดพอคำ 1 จาน\r\n2.	น้ำมันสำหรับทอดและผัด\r\n3.	น้ำซุป 1 ถ้วย\r\n4.	ต้นหอมหั่นหยาบๆ 3 ช้อนโต้ะ\r\n5.	ขิงซอย 3 ช้อนโต้ะ\r\n6.	พริกไทยป่น 1 ช้อนโต้ะ\r\n7.	กระเทียมบด 1 ช้อนโต้ะ\r\n8.	เต้าเจี้ยวบด 2 ช้อนโต้ะ\r\n', 'f042'),
('f043', 'กุ้งมังกรราดซอสข่า', 'C09', 'r2', '1.	เริ่มจากนำกุ้งมังกรไปย่าง ให้มีความสุกประมาณ 70 % ด้วยไฟร้อนปานกลาง จากนั้น แยกเนื้อกุ้งออกจากกระดองและหัวกุ้งออก หั่นเป็นชิ้นพอคำ จากนั้นนำมาพักไว้ก่อน\r\n2.	เตรียมทำซอสข่า โดย ต้มกะทิให้ไฟอ่อน จากนั้นใส่ ข่า ตะไคร้ลงไปต้ม\r\n3.	ใส่ใบมะกรูด ปรุงรสด้วย น้ำตาล เกลือ น้ำปลา และพริกสด ชิมรส ให้ได้รสเค็ม มัน และหอมข่า\r\n4.	จากนั้น กรองเอาแต่น้ำกะทิที่ปรุงรสข่า นำน้ำกะทิที่กรองมาผัดกับน้ำพริกเผาและใส่น้ำมะนาวลงไป\r\n5.	จากนั้นใส่เนื้อกุ้งมันกรลงไปสะดุ้งซอส และนำขึ้นมาจัดเรียงบนจานอาหารใหสวยงาม\r\n6.	นำหัวและหางกุ้งมังกรที่ได้จากการย่างและแยกเนื้อออกมาแต่งจาน เมนูกุ้งมังกรราดซอสขิง ให้สวยงาม แต่งหน้าด้วย พริกซอย ใบมะกรูดและผักชี เสร็ฟพร้อมเสริฟรับประทาน เมนูกุ้งมังกรราซอสขิง\r\n\r\n', '	กุ้งมังกร 1 ตัว\r\n	หัวกะทิ 1 ถ้วยตวง\r\n	ข่าหั่นแว่น 2 ช้อนโต้ะ\r\n	ตะไคร้หั่นและบุบ 2 ช้อนโต้ะ\r\n	ใบมะกรูด 2 ช้อนโต้ะ\r\n	พริกสดบุบ 1 ช้อนโต้ะ\r\n	น้ำปลา 1 ช้อนโต้ะ\r\n	เกลือ 1 หยิบมือ\r\n	น้ำตาล 1 ช้อนชา\r\n	น้ำมะนาว 1 ช้อนโต้ะ\r\n	น้ำพริกเผา 1 ช้อนโต้ะ\r\n', 'f043'),
('f044', 'ขนมเรไรหน้าปู', 'C13', 'r2', '	ผสมแป้งข้าวเจ้าแป้งท้าวยายม่อม ผสมกับน้ำ ละลายให้เข้ากัน จากนั้นนำไปกรองด้วยผ้าขาวบาง\r\n	นำแป้งไปผัดในกระทะให้แป้งจับตัวเป็นก้อน จากนั้นนำแป้งไปบีบในแท่นพิมพ์ให้เป็นก้อนเส้น\r\n	นำไปนึ่งด้วยหม้อหนึ่งเดือดจัด 2 นาที จะได้แป้งเรไรพร้อมรับประทาน\r\n	เตรียมทำน้ำราด ด้วยการโขรก กระเทียมเจียว หอมเจียว เนื้อกุ้ง เนื้อปู ถั่วเขียวนึ่ง และถั่วเขียวคั่ว โขรกให้ส่วนผสมทั้งหมดละเอียดเป็นเนื้อเดียวกัน\r\n	ตั้งกระทะเคี่ยวน้ำกะทิให้แตกมัน จากนั้นใส่เนื้อที่โขรกเอาไว้แล้วลงไปผัด ปรุงรสด้วย น้ำตาล น้ำปลา น้ำมะขามเปี๊ยก น้ำมะนาวและน้ำมะกรูด เคี้ยวสักพัก ก็สามารถนำไปราดหน้าแป้งเรไรรับประทานได้\r\n	จัดจาน ราดนำราดบนแป้งเรไรแต่งหน้าด้วยผักชีโรยหน้า พร้อมรับประทาน ขนมเรไรหน้าปู แสนอร่อย\r\n', '	แป้งข้าวเจ้า 2 ถ้วยตวง\r\n	แป้งท้าวยายม่อม 2 ช้อนโต้ะ\r\n	น้ำเปล่า 2 ถ้วยตวง\r\n	กะทิ 2 ถ้วยตวง\r\n	เนื้อปู 3 ช้อนโต้ะ\r\n	เนื้อกุ้ง 3 ช้อนโต้ะ\r\n	ถั่วเขียวผ่าซีกนึ่ง 2 ช้อนโต้ะ\r\n	หอมเจียว 2 ช้อนโต้ะ\r\n	กระเทียมเจียว 2 ช้อนโต้ะ\r\n	 ถั่วเขียวคั่วป่น 2 ช้อนโต้ะ\r\n	พริกป่นละเอียด 1 ช้อนโต้ะ\r\n	น้ำตาลปี๊บ 2 ช้อนโต้ะ\r\n	น้ำปลา 2 ช้อนโต้ะ\r\n	น้ำมะขามเปี๊ยก 1 ช้อนโต้ะ\r\n	น้ำมะนาว 1 ช้อนโต้ะ\r\n	น้ำมะกรูด 1 ช้อนโต้ะ\r\n\r\n', 'f044'),
('f045', 'แกงไข่ไล่ทุ่ง', 'C02', 'r2', '1.	ต้มน้ำให้เดือด จากนั้นใส่เกลือลงไป นำไข่เป็ดไปต้ม 5 นาที จากนั้นนำไปแช่ในน้ำเย็น ทำการปลอกเปลือกไข่ และจัดเตรียมไข่ใส่จาน\r\n2.	เตรียมน้ำราดโดย ตั้งกระทะน้ำมันให้ร้อน ใส่ ขมิ้นขาว ขิง หอมแดง มะเขือเทศและผลขมิ้นลงไปผัดให้สุก\r\n3.	จากนั้นปรุงรสด้วย พริกแกงเผ็ด น้ำตาลและน้ำปลา ผัดให้ส่วนผสมเข้ากัน ใส่เนื้อหมูสับลงไปผัดก่อน\r\n4.	จากนั้นใส่เนื้อกุ้งลงไปผัด เมื่อกุ้งสุก ใส่ใบมะกรูดและหัวกะทิลงไปผัด ก็จะได้น้ำซอสแสนอร่อย\r\n5.	ราดซอสลงบนไข่ต้มที่เตรียมไว้ จัดหน้าด้วยผักชีและพริกชี้ฟ้าซอย พร้อมรับประทาน\r\n', '	ไข่เป็ดไล่ทุ่ง 4-5 ฟอง\r\n	หมูสับ 3 ช้อนโต้ะ\r\n	เนื้อกุ้งสับ 3 ช้อนโต้ะ\r\n	เนื้อมะเขีอเทศสับ 2 ช้อนโต้ะ\r\n	ขิงสับหยาบๆ 2 ช้อนโต้ะ\r\n	ขมิ้นขาวสับหยาบ 2 ช้อนโต้ะ\r\n	หอมแดงสับหยาบๆ 2 ช้อนโต้ะ\r\n	ผงขมิ้น 1 ช้อนโต้ะ\r\n	ใบมะกรูดซอย 2 ช้อนโต้ะ\r\n	น้ำตาลปี๊บ 1 ช้อนโต้ะ\r\n	น้ำปลา 2 ช้อนโต้ะ\r\n	พริกแกงเผ็ด 2 ช้อนโต้ะ\r\n	หัวกะทิ 2 ช้อนโต้ะ\r\n	พริกสดสับหยาบๆ 2 ช้อนโต้ะ\r\n	น้ำมันพืช 3 ช้อนโต้ะ\r\n\r\n', 'f045'),
('f046', 'ยำดอกขจรกุ้งสด ', 'C06', 'r2', '1.	เตรียมหม้อต้ม นำ ดอกขจร กุ้ง วุ้นเส้นและหมูสับ ลงไปลวกในน้ำเดือดให้สุก จากนั้นนำมาพักไว้ก่อน\r\n2.	เตรียมชามสำหรับปรุงน้ำยำ โดย ใส่ น้ำตาล น้ำปลา พริกสด และน้ำมะนาว ผสมให้ส่วนผสมละลายเข้ากัน จากนั้นใส่ ดอกขจร กุ้ง หมูสับ วุ้นเส้น หอมใหญ่ซอย มะเขือเทศ จากนั้นผสมให้ส่วนผสมเข้ากับน้ำยำ เสริฟใส่จาน พร้อมรับประทาน กับข้าวง่ายๆ เมนูยำ\r\n', '	ดอกขจร 1 ถ้วย\r\n	กุ้งสด 5 ตัว (แกะเปลือกกุ้งออก)\r\n	หมูสับ 5 ช้อนโต้ะ\r\n	หอมใหญ่หั่น 3 ช้อนโต้ะ\r\n	มะเขือเทศหั่น 1 ถ้วย\r\n	วุ้นเส้น 1 ถ้วย\r\n	น้ำปลา 2 ช้อนโต๊ะ\r\n	น้ำตาล 2 ช้อนโต๊ะ\r\n	น้ำมะนาว 2 ช้อนโต๊ะ\r\n	พริกสดบด 2 ช้อนโต้ะ\r\n\r\n', 'f046'),
('f047', 'แกงรัญจวน', 'C04', 'r2', '1.	ต้มน้ำซุปกระดูกหมู ใส่เกลือลงไป เคี่ยวให้น้ำซุปหวาน ประมาณ 45 นาที\r\n2.	ปรุงรส ด้วย หอมแดง กระเทียม พริกสด และน้ำพริกกะปิ ต้มต่อให้รสหวานของหอมแดงและกระเทียมออกรสชาติ\r\n3.	ปิดไฟ จากนั้นใส่ใบโหระพาและน้ำมะนาวลงไป ก็จะได้ แกงรัญจวนแสนอร่อย\r\n\r\n', '	โครงกระดูกหมู สับเป็นท่อนพอคำ 1 ถ้วย\r\n	หอมแดง 1 ช้อนโต้ะ (ทุบพบบุบ)\r\n	กระเทียม 1 ช้อนโต้ะ (ใส่ทั้งกลีบ)\r\n	พริกสด 5-10 เม็ด\r\n	ตะไคร้ซอย 2 ช้อนโต้ะ\r\n	ใบโหระพา 1 กำมือ\r\n	น้ำมะนาว 1 ช้อนโต้ะ\r\n	เกลือ 1 ช้อนชา\r\n	น้ำพริกกะปิ 2 ช้อนโต้ะ \r\n\r\n', 'f047'),
('f048', 'ไก่นึ่งซีอิ้ว', 'C13', 'r2', '1.	ล้างไก่ให้สะอาด จากนั้นนำมาพักใส่จานไว้ก่อน\r\n2.	ปรุงรสซอสนึ่ง ด้วย น้ำมันหอย ซอสปรุงรส ซีอิ้วขาว พริกไทย น้ำตาล ผสมให้ส่วนผสมเข้ากัน จากนั้นราดลงไปในจากไก่ที่เตรียมเอาไว้\r\n3.	จัดขิงซอย คื่นฉ่ายและเห็ดหอม ใส่จานไก่นึ่ง เตรียมนึ่ง\r\n4.	ต้มหม้อนึ่งให้ร้อน จากนั้นนำไก่นึ่งที่เตรียมเอาไว้ ไปนึ่ง ด้วยเวลาประมาณ 15-20 นาที ก็พร้อมเสริฟรับประทาน กับข้าว เมนูนึ่งไก่ ง่ายๆ\r\n', '	เนื้อไก่หั่นเป็นชิ้นพอคำ 1 จาน\r\n	ซีอิ้วขาว 2 ช้อนโตะ\r\n	ซอสหอยนางรม 1 ช้อนโต้ะ\r\n	น้ำตาล 1 ช้อนโต้ะ\r\n	ซอสปรุงรส 1 ช้อนโต้ะ\r\n	พริกไทยป่น 1 ช้อนโต้ะ\r\n	ขิงซอย 3 ช้อนโต้ะ\r\n	เห็ดหอมซอย 1 ช้อนโต้ะ\r\n	คื่นฉ่าย หั่นหยาบๅ 2 ช้อนโต้ะ\r\n', 'f048'),
('f049', 'แกงชักส้มดอกมะขามอ่อน', 'C04', 'r2', '1.	เตรียมพริกแกง โดย โขรก พริกแห้ง ข่า ตะไคร้ หอมแดง ผิวมะกรูด และกระเทียม ให้ละเอียด จากนั้นพักไว้ก่อน\r\n2.	ล้างปลาช่อนให้สะอาด จากนั้นนำเนื้อปลาช่อนลงไปนึ่ง ประมาณ 15 นาทีให้เนื้อปลาสุก จากนั้นนำไปพักไว้ก่อน\r\n3.	นำผักบุ้งจีนลงไปลวก เมื่อสุกแล้ว นำไปแช่ในน้ำเย็น นำผัดบุ้งแต่ละท่อนมามัดเป็นเหมือนเงื่อน ท่อนละ 1 อัน\r\n4.	นำปลาอินทรีย์เค็มมาทอด จากนั้นแกะเอาเฉพาะเนื้อมาทำอาหาร\r\n5.	ตั้งกระทะให้ร้อนปานกลาง จากนั้นใส่กะทิลงไปต้ม ตามด้วยฝักมะขามอ่อนและอินทรีย์เค้มทอดลงไป พร้อมด้วยพริกแกงที่เตรียมไว้ เมื่อพริกแกงสุกมีกลิ่นหอม ใส่เนื้อปลาช่อน พร้อมด้วยน้ำที่ได้จากการนึ่งปลาช่อนลงไปด้วย\r\n6.	ปรุงรสด้วยการใส่ น้ำปลา น้ำตาลทราย น้ำตาลปี๊บ น้ำมะขามเปียก เกลือและน้ำมะกรูด ลงไป ตามด้วยผักบุ้ง ใบมะกรูดและดอกมะขามอ่อนลงไป เป็นอันเสร็จ สำหรับแกงชักส้มดอกมะขามอ่อน\r\n', '	พริกแห้งเม็ดใหญ่ซอย 2 ช้อนโต้ะ\r\n	หัวหอมแดงซอย 2 ช้อนโต้ะ\r\n	กระเทียมซอย 2 ช้อนโต้ะ\r\n	ตะไคร้ซอย 2 ช้อนโต้ะ\r\n	ข่าซอย 1 ช้อนโต้ะ\r\n	ปลาช่อนหั่นเป็นท่อน 1 ตัว\r\n	ผักบุ้งจีนหั่นเป็นท่อนประมาณ 10 เซ็นติเมตร 1 ถ้วย\r\n	ฝักมะขามอ่อนทุบพอบุบ 2 ผัก\r\n	หัวกะทิ 1 ถ้วยตวง\r\n	ผิวมะกรูด 1 ช้อนโต้ะ\r\n	น้ำมะกรูด 2 ช้อนโต้ะ\r\n	ใบมะกรูดซอยหยาบๆ 2 ใบ\r\n	ดอกมะขามอ่อน 1 กำมือ\r\n	ปลาอินทรีย์เค็ม 1 ชิ้น\r\n	น้ำมะขามเปียก 1 ช้อนโต้ะ\r\n	น้ำตาลทราย 1 ช้อนโต้ะ\r\n	น้ำตาลปี๊บ 1 ช้อนโต้ะ\r\n	น้ำปลา 2 ช้อนโต้ะ\r\n	เกลือ 1 ช้อนชา\r\n', 'f049'),
('f050', 'ยำสะนัด ', 'C06', 'r1', '1.	เตรียมเครื่องแกง โดยโขลก พริกเผา ข่า หอมแดง กระเทียมและกะปิ ให้ละเอียดเข้ากัน\r\n2.	ลวก ถั่วฝักยาว ผักบุ้ง หัวปลี มะเขือพวง และ หมูสับให้สุก\r\n3.	ตั้งกระทะ นำกระเทียมลงไปผัดกับเครื่องแกงให้สุก จากนั้นนำผักลวกและหมูสับลงไปผัด ตามด้วยงาคั่ว และใบหอม\r\n4.	เสริฟใส่จาน โรยด้วยหอมเจียว กับข้าวง่ายๆ เมนู อาหารไทย 4 ภาค อาหารเหนือ\r\n', '	หมูสับ 4 ช้อนโต้ะ\r\n	ถั่วฝักยาวหั่นเล็กๆ 5 ช้อนโต้ะ\r\n	ผักบุ้งหั่นเล็กๆ 5 ช้อนโต้ะ\r\n	หัวปลีซอย 1 ถ้วย\r\n	มะเขือพวง 1 ถ้วย\r\n	งาคั่ว 2 ช้อนโต๊ะ\r\n	กระเทียมบด 1 ช้อนโต๊ะ\r\n	ใบหอมซอย 2 ช้อนโต๊ะ\r\n	หอมแดงเจียว 1 ถ้วย\r\n	น้ำปลาร้า 1 ถ้วย\r\n	พริกแห้งเผา 7 เม็ด\r\n	ข่าหั่น 2 ช้อนโต๊ะ\r\n	หอมแดง 4 หัว\r\n	กระเทียม 2 หัว\r\n	กะปิ 1 ช้อนโต๊ะ\r\n', 'Fn1'),
('f051', 'ยำจิ้นไก่', 'C06', 'r1', '1.	เตรียมครก โขลก ข่า ตะไคร้ หอมแดง และ พริกแห้ง ให้ละเอียด\r\n2.	ต้มน้ำซุปไก่ และนำเครื่องแกงลงไปผสม จากนั้นนำ หัวปลี เห็ด และเนื้อไก่ลงไปต้ม\r\n3.	เสริฟใส่ถ้วย โรยด้วยต้นหอมผักชี กับข้าวง่ายๆ เมนูยำ\r\n', '	เนื้อไก่ฉีกเป็นชิ้น 1 ถ้วย\r\n	เห็ดฟาง 1 ถ้วย\r\n	หัวปลีหั่น 1 ถ้วย\r\n	ข่าซอย 1 ช้อนชา\r\n	ตะไคร้ซอย 2 ช้อนโต้ะ\r\n	หอมแดงซอย 3 ช้อนโต้ะ\r\n	กระเทียม 2 หัว\r\n	พริกแห้ง 5 เม็ด\r\n	ผักชีต้นหอมซอย 2 ช้อนโต๊ะ\r\n	น้ำซุปไก่ 3 ถ้วย\r\n', 'Fn10'),
('f052', 'แกงผักปั๋งใส่แหนม ', 'C04', 'r1', '1.	เตรียมครก โขลก หอมแดง กระเทียม กะปิและปลาร้า ให้ละเอียดเข้ากัน จากนั้นนำไปต้มในน้ำซุปหมู\r\n2.	ใส่ผักปั๋ง น้ำมะนาว มะเขือเทศ พริกหนุ่ม ต้นหอมผักชีและแหนมลงไป\r\n3.	ปิดไฟเสริฟใส่ชาม พร้อม กับข้าวง่ายๆ\r\n', '	ยอดอ่อนผักปั๋ง 4 ถ้วย\r\n	แหนมซอยเป็นชิ้น 1 ถ้วย\r\n	พริกหนุ่มหั่นเป้นท่อน 4 ช้อนโต้ะ\r\n	หอมแดง 3 หัว\r\n	มะเขือเทศ หั่นครึ่ง 3 ลูก\r\n	กระเทียม 2 หัว\r\n	กะปิ 1/2 ช้อนโต้ะ\r\n	ปลาร้า 1 ช้อนชา\r\n	น้ำ มะนาว 2 ช้อนโต๊ะ\r\n	เกลือ 1 ช้อนชา\r\n	ต้นหอมผักชีซอย 1/2 ถ้วย\r\n	น้ำซุปหมู 4 ถ้วย\r\n', 'Fn11'),
('f053', 'น้ำพริกอ่อง ', 'C05', 'r1', '1.	นำ หอมแดง, กะปิ, กระเทียม, เต้าเจี้ยว, พริก, ตะไคร้ และน้ำเปล่า โครกให้ละเอียดเป็นเนื้อเดียวกัน\r\n2.	ตั้งกระทะน้ำมัน นำพริกแกงที่โครกไว้ ลงไปผัดให้หอม จากนั้นนำหมูสับ มะเขือเทศ น้ำตาลเกลือ และน้ำเปล่านิดหน่อยลงไปผัด จนสุก\r\n3.	เสริฟใส่ถ้วยน้ำจิ้ม เสริฟกับผักเคียง\r\n', '	หมูสับ 3 ขีด\r\n	มะเขือเทศ 2 ขีด\r\n	หอมแดงหั่นเป็นชิ้นๆ 3 ช้อนโต้ะ\r\n	พริกแห้งหั่นเป็นชิ้นๆ 2 ช้อนโต้ะ\r\n	ตะไคร้หั่นละเอียด 2 ช้อนโต้ะ\r\n	กะปิ 1 ช้อนโต้ะ\r\n	เต้าเจี้ยว 1 ช้อนชา\r\n	กระเทียมสับละเอียด 2 ช้อนโต้ะ\r\n	เกลือ 1 ช้อนชา\r\n	น้ำตาล 1 ช้อนชา\r\n	ผักชีสำหรับแต่งอาหาร\r\n	ผักสด เช่น แตงกวา มะเขือ ผัดกาดหอม\r\n	น้ำมันพืช 2 ช้อนโต้ะ\r\n	น้ำเปล่า 5 ช้อนโต้ะ\r\n', 'Fn12'),
('f054', 'แกงแคไก่ ', 'C04', 'r1', '1.	เตรียมเครื่องแกงดดย โขลกส่วนผสมเครื่องแกงทั้งหมดให้ละเอียดเข้ากัน จากนั้นตั้งกระทะน้ำมันให้ร้อนนำไก่และเครื่องแกงที่เตรียมไว้ลงไปผัดให้สุกหอม\r\n2.	จากนั้นเติมน้ำซุปลงไปต้มต่อ ตามด้วยผักที่เตรียมไว้ทั้งหมด ประกอบด้วย ผักตำลึง มะพร้าวอ่อน ผักเผ็ด ถั่วพู ถั่วฝักยาว มะเขือพวง มะเขือกรอบ ใบชะพลลู ผักชีฝรั่ง ผักขั้หูด เห็ดลม\r\n3.	ชิมรสตามใจชอบ เสริฟใส่จาน แกงแคไก่ แกงแค วิธีทําแกงแคไก่ เมนูอาหารเหนือ วิธีทำแกงแค สูตรแกงแค วิธีทําแกงแค คั่วแคไก่\r\n', '	เนื้อไก่หั้นให้พอคำ 1 ตัว\r\n	น้ำมัน 2 ช้อนโต๊ะ\r\n	ผักตำลึง 2 ถ้วย\r\n	มะพร้าวอ่อนซอย 1 ถ้วย\r\n	ผักเผ็ด 1 ถ้วย\r\n	ใบชะอม 1 ถ้วย\r\n	ถั่วพู ครึ่งถ้วย\r\n	ถั่วฝักยาวซอยหยาบ ครึ่งถ้วย\r\n	มะเขือพวง ครึ่งถ้วย\r\n	มะเขือกรอบ ครึ่งถ้วย\r\n	ใบชะพลูหั่นหยาบ ครึ่งถ้วย\r\n	ผักชีฝรั่งหั่นหยาบ ครึ่งถ้วย\r\n	ผักขี้หูด ครึ่งถ้วย\r\n	เห็ดลมหั่นหยาบ 5 ช้อนโต้ะ\r\n	น้ำซุป 3 ถ้วย\r\n	เครื่องแกง(พริกแห้ง 4 เม็ด ข่าหั่นละเอียด 1 ช้อนโต๊ะ ตะไคร้ซอย 2 ช้อนโต๊ะ หอมแดง 4 หัว กระเทียม 2 หัว กะปิ 1 ช้อนโต๊ะ รากผักชี 1 ช้อนชา ปลาร้าสับ 2 ช้อนโต๊ะ )\r\n', 'Fn13'),
('f055', 'แกงกระด้าง ', 'C04', 'r1', '1.	เตรียมครก โขลกกระเทียม หอมแดง รากผักชี เกลือ พริกไทย ให้ละเอียดเข้ากัน\r\n2.	ต้มน้ำให้เดือด จากนั้นใส่เครื่องที่โขลกลงไปต้ม พร้อมกับเนื้อหมู เติมน้ำปลาลงไป เคี่ยวให้เนื้อหมูเปื่อย\r\n3.	เสริฟใส่ชามทิ้งไว้ให้เย็น จากนั้นนำไปแช่เย็นในตู้เย็น ให้น้ำซุปจับตัวเป็นวุ้น\r\n4.	โรยหน้าด้วยผักชี พร้อมรับประทาน\r\n', '	เนื้อหมูหั่นเป้นชิ้นพอคำ 1 ถ้วย\r\n	หอมแดง 4 หัว\r\n	กระเทียม 3 หัว\r\n	พริกไทยป่น 1 ช้อนชา\r\n	รากผักชี 2 ต้น\r\n	เกลือ 1 ช้อนชา\r\n	น้ำปลา 1 ช้อนโต๊ะ\r\n	ผักชีหั่นเป็นฝอย 1 ช้อนโต๊ะ\r\n', 'Fn14'),
('f056', 'แกงโฮะ ', 'C04', 'r1', '1.	ตั้งกระทะน้ำมัน ผัดกระเทียมให้หอม ใส่พริกแกงเผ็ดและพริกแกงฮังเล ลงไปผัด\r\n2.	ตามด้วย แหนม หน่อไม้ดอง แคบหมู ใบมะกรูด ผักตำลึง ตะไคร้ ปรุงรส ด้วยน้ำปลา และน้ำมะนาว ผัดให้เข้ากัน\r\n3.	นำวุ้นเส้นลงไปผัดต่อให้วุ้นเส้นสุก เสริฟใส่จาน รับประทานได้ กับข้าวง่ายๆ อาหารเหนือ\r\n', '	พริกแกงเผ็ด 1 ถ้วย\r\n	พริกแกงฮังเล 1 ถ้วย\r\n	แหนมหม้อ ขยี่ให้กระจาย 2 ถ้วย\r\n	หน่อไม้ดองหั่นบางๆ 1 ถ้วย\r\n	แคบหมู 1 ถ้วย\r\n	วุ้นเส้น 1 ถ้วย\r\n	ใบมะกรูดฉีก 4-5 ใบ\r\n	ผักตำลึงหั่นยาว 1 ถ้วย\r\n	ต้นตะไคร้หั่นฝอย 2 ต้น\r\n	น้ำปลา 1 ช้อนโต้ะ\r\n	น้ำมะนาว 1 ช้อนโต้ะ\r\n	พริกสดบด 2 ช้อนโต๊ะ\r\n	กระเทียมบด 2 ช้อนโต๊ะ\r\n	มันพืช 3 ช้อนโต้ะ\r\n', 'Fn15'),
('f057', 'แกงฮังเล ', 'C04', 'r1', '	เนื้อขาหมู 1 กิโลกรัม\r\n	น้ำพริกแกงเผ็ด 3 ช้อนโต้ะ\r\n	ผงฮังเล 1 ช้อนชา\r\n	เกลือ 1 ช้อนชา\r\n	ซีอิ๊วดำ 1 ช้อนชา\r\n	ซีอิ๊วขาว 2 ช้อนโต๊ะ\r\n	น้ำปลา 2 ช้อนโต๊ะ\r\n	ขิงซอย 3 ช้อนโต้ะ\r\n	หัวหอมซอย 3 ช้อนโต้ะ\r\n	กระเทียมบด 2 ช้อน\r\n	น้ำตาลปี๊บ 3 ช้อนโต๊ะ\r\n	น้ำมะขามเปียก 1 ถ้วย\r\n	น้ำซุป 4 ถ้วย\r\n	กระท้อนเปรี้ยวสับ 1 ถ้วย\r\n', '1.	หมักขาหมูด้วย พริกแกงเผ็ด ผงฮังเล ซีอิ้วดำ ซีอิ้วขาว น้ำปลา และเกลือ หมักสัก 1 ชั่วโมง\r\n2.	ตั้งหม้อนำน้ำซุปลงไปต้ม ใส่กะท้อนสับลงไป ตามด้วย น้ำตาล หัวหอม ขิง และกระเทียม จากนั้นนำขาหมูลงไปต้ม\r\n3.	เคี่ยวจนเนื้อนิ่มเปื่อย เสริฟใส่ชาม กับข้าวง่ายๆ เมนูเหนือ อาหารไทย ง่ายๆ\r\n', 'Fn16'),
('f058', 'ข้าวผัดหมูลิ้นจี่ ', 'C02', 'r1', '1.	นำข้าวไปแช่ตู้เย็น ให้ข้าวเย็นจับตัวเป็นเม็ดก่อน 15 นาที\r\n2.	ตั้งกระทะน้ำมัน ให้ร้อน จากนั้นใส่กระเทียม ไข่ไก่และหมูลงไปผัด พอหมูสุก และไข่สุก ก็ใส่ข้าวลงไปผัด\r\n3.	ปรุงรสด้วย ซอสปรุงรส และน้ำตาล ผัดให้ส่วนผสมเข้ากัน ใส่ต้นหอมและลิ้นจี่ลงไปผัด\r\n4.	เสริฟข้าวผัดหมูลิ้นจี้  ทานคู่กับ แตงกวา ต้นหอม มะเขือ และพริกน้ำปลา \r\n', '	ข้าวหุงสุก 1 ถ้วย\r\n	ไข่ไก่ 1 ฟอง\r\n	เนื้อลิ้นจี่สด 3 ลูก\r\n	หมูสดหั่นเป็นชื้นพอคำ 4 ช้อนโต้ะ\r\n	กระเทียมบด 1 ช้อนชา\r\n	ต้นหอมซอย 1 ช้อนโต้ะ\r\n	ซอสปรุงรส 1 ช้อนโต้ะ\r\n	น้ำตาล 1 ช้อนชา\r\n	น้ำมันพืช 2 ช้อนโต้ะ\r\n	พริกน้ำปลา 1 ถ้วย\r\n', 'Fn17'),
('f059', 'กระบอง ', 'C03', 'r1', '1. หั่นฟักทองเป็นแผ่นบาง หั่นเป็นเส้นๆ\r\n2. นวดแป้งข้าวเจ้ากับน้ำกะทิ ใส่น้ำกะทิทีละน้อย นวดจนแป้งกับกะทิเข้ากัน \r\n3. ใส่มะพร้าวขูดขาว \r\n4. ใส่น้ำพริกแกงเผ็ด น้ำตาลทราย (1 ช้อนโต๊ะ) เกลือป่น (1 ช้อนชา) คนให้เข้า\r\nกัน\r\n5. ใส่ฟักทองลงคลุกกับส่วนผสม\r\n6. ตั้งกระทะ ใส่น้ำมัน พอร้อน ตักส่วนผสม 1 ช้อน ลงทอดจนเหลืองทั่ว \r\n7. ใช้ตระแกรงตักขึ้น ให้สะเด็ดน้ำมัน \r\n8. การทำน้ำจิ้ม ผสมน้ำส้มสายชู น้ำตาล (1 ถ้วย) เกลือป่น (1 ช้อน \r\nชา) พริกชี้ฟ้าแดงโขลก ในหม้อรวมกัน นำไปตั้งไฟให้ละลาย เคี่ยวต่อจน\r\nงวด ปิดไฟ \r\n', '1.	ฟักทอง\r\n300	กรัม\r\n2.	แป้งข้าวเจ้า	2	ถ้วย\r\n3.	น้ำพริกแกงเผ็ด	1	ช้อนโต๊ะ\r\n4.	มะพร้าว\r\n1	ถ้วย\r\n5.	น้ำตาลทราย	1	ช้อนโต๊ะ\r\n6.	กะทิ	1	ถ้วย\r\n7.	เกลือ	1	ช้อนชา\r\n8.	น้ำส้มสายชู	1	ถ้วย\r\n9.	พริกชี้ฟ้าแดง\r\n1	เม็ด\r\n10.	น้ำตาลทราย	1	ถ้วย\r\n11.	เกลือ	1	ช้อนชา\r\n12.	ถั่วลิสงคั่ว\r\n2	ช้อนโต๊ะ\r\n', 'Dn1'),
('f060', 'ข้าวหนุกงา', 'C13', 'r1', '1. ข้าวสารเหนียวล้างให้สะอาด แช่น้ำไว้ 1 คืน\r\n2. ล้างข้าวที่แช่ไว้ นึ่งไฟกลางประมาณ 25 - 30 นาที\r\n3. พอข้าวนึ่งสุก เทลงในภาชนะ คนข้าวให้ไอร้อนลดลง ก่อนบรรจุไว้ในกล่องข้าว เพื่อเก็บความร้อน \r\n4. คั่วงาขี้ม้อน ใช้ไฟอ่อนๆ จนมีกลิ่นหอม\r\n5. โขลกงาขี้ม้อนและเกลือให้ละเอียด \r\n6. ใส่ข้าวเหนียวร้อนๆ ลงโขลกรวมกัน \r\n7. นวดข้าวหนุกงาจนเป็นเนื้อเดียวกัน\r\n', '1.	ข้าวสารเหนียว1	ลิตร\r\n2.	งาขี้ม้อน1/2	ถ้วย\r\n3.	เกลือป่น	1	ช้อนโต๊ะ\r\n', 'Dn11'),
('f061', 'ขนมตาล', 'C13', 'r1', '1. นำผลตาลสุกงอม ปอกเปลือกแช่น้ำ ยีลูกในน้ำที่แช่ นำเนื้อตาล ใส่ในถุงผ้าแขวนไว้จนน้ำออกหมด เหลือแต่เนื้อตาล และนำมาผสมกับแป้งข้าวเจ้า นวดให้เข้ากัน ใส่แป้ง\r\n2. ใส่น้ำกะทิ นวดให้เป็นเนื้อเดียวกัน นวดแป้ง\r\n3. ใส่น้ำตาลทราย นวดต่อจนน้ำตาลละลาย หมักไว้ 1 ชั่วโมง ใส่น้ำตาลทราย\r\n4. เตรียมกระทงใบตอง ตัดเป็นรูปวงกลมเส้นผ่าศูนย์กลางยาว 4 นิ้ว เย็บสี่มุมเป็นรูปกระทง ทำกระทง\r\n5. ตักขนมใส่กระทง ตักขนมใส่กระทง\r\n6. โรยมะพร้าวขูดลงบนหน้าขนม โรยมะพร้าว\r\n7. นำไปนึ่ง ใช้เวลาประมาณ 20 นาที นึ่งขนม\r\n', '1.	ตาลสุก 400	กรัม\r\n2.	แป้งข้าวเจ้า	600	กรัม\r\n3.	น้ำตาลทราย	500	กรัม\r\n4.	กะทิ	2	ถ้วย\r\n5.	เกลือป่น	1/2	ช้อนชา\r\n', 'Dn12'),
('f062', 'ข้าวหลาม\r\n', 'C10', 'r1', '1. ตัดไผ่ข้าวหลามให้ยาวประมาณ 12 นิ้ว ล้างเฉพาะด้านนอกกระบอก ให้สะอาด คว่ำกระบอกลง พักไว้ให้แห้ง \r\n2. ผสมกะทิ น้ำตาล และเกลือ รวมกันแล้วคนให้เข้ากันจนน้ำตาลละลาย \r\n3. ล้างข้าวสารให้สะอาดจนกระทั่งน้ำใส นำข้าวใส่ตระกร้าเพื่อให้สะเด็ดน้ำ ใส่ถั่วดำต้มสุกลงในข้าว คลุกเคล้าให้เข้ากัน\r\n4. นำข้าวที่ผสมถั่วดำแล้วใส่ลงในกระบอก 1 กำมือ กระแทกเบาๆ ทำสลับกันต่อไปเรื่อยๆ จนเต็มกระบอก เลือกด้านบนกระบอกไว้ประมาณ 2 นิ้ว สำหรับปิดจุก\r\n5. เทน้ำกะทิที่ผสมแล้ว ใส่ลงไปในกระบอกข้าวทีละน้อย จนกระทั่งน้ำกะทิท่วมข้าว \r\n6. พับใบเตยเป็นรูปสี่เหลี่ยม ปิดกระบอกข้าวหลาม แล้วนำกาบมะพร้าวม้วนเป็นทรงกลมมาปิดอีกชั้นหนึ่ง ทิ้งไว้ประมาณ 5- 6 ชั่วโมง\r\n7. เผาข้าวหลามกับถ่านไม้ พอกระบอกเหลืองให้หมุนกระบอกข้าวหลาม ใช้เวลาประมาณ 30-45 นาที สังเกตกระบอกมีสีเหลืองทั่ว แสดงว่าข้าวหลามสุก\r\n8. ทิ้งไว้ให้อุ่น ปอกเปลือก และเหลาให้เปลือกข้าวหลามบางลง เพื่อให้แกะรับประทานได้ง่าย \r\n', '1.	ข้าวสารเหนียว	1 1/2	ลิตร\r\n2.	น้ำตาลทราย	400	กรัม\r\n3.	กะทิ	3	ถ้วย\r\n4.	ถั่วดำถั่วดำต้มสุก\r\n2	ถ้วย\r\n5.	เกลือป่น	2	ช้อนโต๊ะ\r\n6.	ใบเตย	12	ใบ\r\n7.	ไผ่ข้าวหลาม\r\n12	กระบอก\r\n8.	กาบมะพร้าว	12	ชิ้น\r\n', 'Dn13'),
('f063', 'ขนมฟักทอง', 'C13', 'r1', '1.	ฟักทอง\r\n1	กิโลกรัม\r\n2.	น้ำตาลทราย	500	กรัม\r\n3.	แป้งข้าวเจ้า	500	กรัม\r\n4.	แป้งมัน	200	กรัม\r\n5.	มะพร้าวมะพร้าวขูด\r\n1	ถ้วย\r\n6.	เกลือป่น	1	ช้อนชา\r\n7.	กะทิ	2	ถ้วย\r\n', '1. นึ่งฟักทองให้สุก นำมานวดให้เข้ากันกับแป้งข้าวเจ้า แป้งมัน และกะทิ \r\n2. ใส่น้ำตาล และเกลือ \r\n3. นวดส่วนผสมทั้งหมดให้เป็นเนื้อเดียวกัน พักไว้  \r\n4. เตรียมกระทงใบตอง ตัดเป็นรูปวงกลมเส้นผ่าศูนย์กลางยาว 4 นิ้ว เย็บสี่มุมเป็นรูปกระทง ตักขนมใส่ในใบตอง  \r\n5. โรยมะพร้าวขูดลงบนหน้าขนม  \r\n6. นึ่งไฟกลาง ใช้เวลาประมาณ 15-20 นาที\r\n', 'Dn14'),
('f064', 'ขนมเทียน ', 'C13', 'r1', '1. นำแป้งข้าวเจ้า น้ำอ้อย และเกลือ นวดให้เข้ากัน ใส่น้ำตาล\r\n2. นวดส่วนผสมจนเป็นเนื้อเดียวกัน นวดส่วนผสม\r\n3. นำไปตั้งไฟ ใช้ไม้พายกวนขนม โดยใช้ไฟอ่อนๆ กวนขนม\r\n4. เตรียมใบตองเป็นรูปทรงกรวย ใส่ขนมลงในกรวย พับใบตองด้านบนแล้วใช้ไม้กลัด ใส่ขนมลงในกรวย\r\n5. นำไปนึ่ง ใช้เวลาประมาณ 20 นาที นึ่งขนม\r\n', '1.	แป้งข้าวเจ้า	500	กรัม\r\n2.	น้ำอ้อย	200	กรัม\r\n\r\n3.	เกลือ	1	ช้อนชา\r\n', 'Dn2'),
('f065', 'ขนมแตงไทย ', 'C13', 'r1', '1. ปอกเปลือกแตงไทย เอาไส้ออก ใส่ภาชนะ ใส่แป้งข้าวจ้าว \r\n2. ใส่น้ำกะทิ คนให้เข้ากัน \r\n3. นวดส่วนผสมทั้งหมดให้เข้ากัน และขยำแตงไทยให้เป็นชิ้นเล็ก ๆ \r\n4. ใส่น้ำตาลทราย แล้วนวดให้เข้ากันจนน้ำตาลละลาย \r\n5. ฉีกใบตองกว้าง 6 นิ้ว ทำเป็นกรวย แล้วใช้ไม้กลัด กลัดใบตอง \r\n6. ตักส่วนผสมใส่ลงในกรวยให้เต็ม \r\n7. วางกรวยขนมในรูของชั้นลังถึง \r\n8. โรยมะพร้าวขูด แล้วนำไปนึ่ง ใช้เวลาประมาณ 20 นาที\r\n', '1.	แตงไทย 500	กรัม\r\n\r\n2.	น้ำตาลทราย	500	กรัม\r\n3.	แป้งข้าวเจ้า	400	กรัม\r\n4.	แป้งมัน	100	กรัม\r\n5.	มะพร้าว\r\n1	ถ้วย\r\n6.	กะทิ	3	ถ้วย\r\n7.	เกลือป่น	1	ช้อนชา\r\n', 'Dn3'),
('f066', 'ขนมกนน้ำอ้อย', 'C01', 'r1', '1. ผสมน้ำ น้ำกะทิ และน้ำใบเตย ให้เข้ากัน\r\n2. ใส่มะพร้าวทึนทึกขูด คนให้เข้ากัน\r\n3. ใส่แป้งข้าวเหนียว คนให้แป้งละลาย \r\n4. ใส่น้ำอ้อย คนให้เข้ากัน\r\n5. นำส่วนผสมไปตั้งไฟ ให้ใช้ไฟอ่อน กวนจนแป้งสุกและเหนียว\r\n6. ทิ้งไว้ให้เย็น นำมาห่อใบตองเป็นรูปสี่เหลี่ยม\r\n7. นึ่งไฟกลาง ประมาณ 30 นาที\r\n', '\r\n1.	แป้งข้าวเหนียวขาว	1	กิโลกรัม\r\n2.	น้ำอ้อย	1	กิโลกรัม\r\n\r\n3.	มะพร้าวทึนทึก\r\n2	ถ้วย\r\n4.	น้ำใบเตย	1	ถ้วย\r\n5.	น้ำเปล่า	2	ถ้วย\r\n', 'Dn4'),
('f067', 'ข้าวต้มหัวหงอก', 'C01', 'r1', '1. แช่ข้าวสารเหนียวในน้ำประมาณ 2-3 ชั่วโมง ล้างให้สะอาด \r\n2. ใส่น้ำคั้นใบเตย และเกลือ คลุกเคล้าให้เข้ากัน พักไว้ประมาณ 10 นาที ตักขึ้นให้เสด็จน้ำ\r\n3. นำใบตองกว้างประมาณ 10 นิ้ว ซ้อนไคว้กัน 2 ชั้น ใส่ข้าวเหนียว 1 กำมือ วางกล้วยน้ำว้าตรงกลาง แล้วใส่ข้าวอีก 1 กำมือ \r\n4. ห่อใบตองให้แน่น พับหัวและพับท้าย มัดด้วยตอกให้แน่น\r\n5. นำไปต้ม ประมาณ 1 ชั่วโมง\r\n6. ต้มสุกแล้ว ทิ้งไว้ให้เย็น แกะห่อออก หั่นเป็นชิ้นๆ \r\n7. คลุกกับมะพร้าวขูด โรยด้วยน้ำตาลทราย\r\n', '1.	ข้าวสารเหนียว	5ถ้วย\r\n2.	กล้วยน้ำว้าสุก	10	ลูก\r\n3.	น้ำตาลทราย	2	ช้อนโต๊ะ\r\n4.	มะพร้าวมะพร้าวขูด\r\n1	ถ้วย\r\n5.	เกลือป่น	1	ช้อนชา\r\n6.	น้ำใบเตย	1	ถ้วย\r\n', 'Dn6'),
('f068', 'ขนมกล้วย\r\n', 'C13', 'r1', '1. แกะเปลือกกล้วยน้ำว้า ใส่ภาชนะ ใส่แป้งข้าวเจ้า \r\n2. ใส่น้ำกะทิ คนให้เข้ากัน \r\n3. นวดส่วนผสมทั้งหมดให้เข้ากันและขยำกล้วยให้เป็นชิ้นเล็ก ๆ \r\n4. ใส่น้ำตาลทราย แล้วนวดให้เข้ากันจนน้ำตาลละลาย \r\n5. ฉีกใบตองกว้าง 6 นิ้ว ทำเป็นกรวย แล้วใช้ไม้กลัด กลัดใบตอง \r\n6. ตักส่วนผสมใส่ลงในกรวยให้เต็ม \r\n7. วางกรวยขนมในรูของลังถึง \r\n8. โรยมะพร้าวขูด นึ่งไฟกลาง ประมาณ 20 นาที\r\n', '1.	กล้วยน้ำว้าสุก500	กรัม\r\n2.	น้ำตาลทราย	500	กรัม\r\n3.	แป้งข้าวเจ้า	300	กรัม\r\n4.	แป้งมัน	100	กรัม\r\n5.	มะพร้าว\r\n1	ถ้วย\r\n6.	กะทิ	3	ถ้วย\r\n7.	เกลือป่น	1	ช้อนชา\r\n', 'Dn7'),
('f069', '', 'C12', 'r1', '1 ใส่น้ำลงในข้าวนึ่งสุก ใช้ทัพพีบี้ข้าวให้แตกออกจากกัน \r\n2 ใส่น้ำตาลทราย เกลือ และน้ำอ้อย คนให้เข้ากัน\r\n3 นำส่วนผสมตั้งไฟ คนไปเรื่อยๆ ถ้าน้ำแห้งเติมน้ำเล็กน้อย จนกระทั่งน้ำอ้อยละลาย และเข้ากันดี \r\n4 ทาน้ำมันให้ทั่วถาด ก่อนเทข้าวเหนียวลงไป\r\n5 เทข้าวเหนียวลงในถาด เกลี่ยจนเต็มถาด\r\n6. โรยงาให้ทั่ว ทิ้งไว้ให้แห้ง \r\n', '1.	ข้าวสารเหนียว2	ถ้วย\r\n2.	น้ำตาลทราย	2	ช้อนโต๊ะ\r\n3.	น้ำอ้อยป่น	1	ถ้วย\r\n4.	งา\r\n2	ช้อนโต๊ะ\r\n5.	เกลือป่น	1	ช้อนโต๊ะ\r\n6.	น้ำเปล่า	1	ถ้วย\r\n', 'Dn8'),
('f070', 'ขนมจ็อก', 'C13', 'r1', '1. ทำไส้ขนม นำมะพร้าวขูดลงผัดในกระทะ ใช้ไฟปานกลาง ผัดพอหอม ใส่เกลือ 1 ช้อน\r\nโต๊ะ ผัดให้เข้ากัน \r\n2. ผัดไส้มะพร้าวจนเหนียวดีแล้ว พักไว้ \r\n3. ทำแป้งขนม นำแป้งนวดผสมกับน้ำ และเกลือป่น 1 ช้อนชา ให้เข้ากันจนเป็นก้อน \r\n4. ฉีกใบตองกว้างประมาณ 10 ซม. นำด้านที่ห่อมาทาน้ำมันให้ทั่ว \r\n5. นำแป้งที่นวดมาทำเป็นแผ่นกลม ใส่ไส้มะพร้าวตรงกลาง แล้วห่อแป้งให้มิดไส้ แล้วคลึง\r\nเป็นก้อนกลม \r\n6. ทำใบตองเป็นรูปทรงกรวย ใส่ขนมลงในใบตอง พับทบล่าง ซ้าย ขวา นำด้านที่แหลมสอด\r\nพับ แล้วห่อจะได้ขนมทรงสามเหลี่ยม \r\n7. นำไปนึ่งด้วยไฟแรงประมาณ 20 นาที  \r\n', '1.	แป้งข้าวเหนียวดำ300	กรัม\r\n2.	น้ำตาลปี๊บ	1	ถ้วย\r\n3.	เกลือป่น	1	ช้อนโต๊ะ\r\n4.	เกลือป่น	1	ช้อนชา\r\n5.	น้ำมันพืช	2	ช้อนโต๊ะ\r\n', 'Dn9'),
('f071', 'ขนมลา', 'C03', 'r4', '•	ล้างข้าวเจ้าให้สะอาดแล้วหมักลงกระสอบจูดทิ้งไว้ 2 คืน ครบกำหนดนำออกล้างให้หมดกลิ่น โม่เป็นแป้งแล้วบรรจุลงถุงผ้าบางๆ แขวนหรือวางไว้ให้สะเด็ดน้ำ พอหมาดนำไปวางราบลง หาของหนักๆ วางทับเพื่อให้แห้งสนิท นำแป้งที่แห้งแล้วนั้นไปตำให้ร่วน ใส่น้ำผึ้งคลุกเคล้าจนเข้ากันดี เอามือจุ่มโรย(ทอด)ดู เมื่อเห็นว่าเป็นเส้นดีและโรยได้ไม่ขาดสายก็ใช้ได้ ลองชิมดูรสจนเป็นที่พอใจ\r\n•	โรยทอดด้วยกระทะขนาดใหญ่ ไฟอ่อนๆ เอาน้ำมันผสมไข่แดงทาให้ทั่วกระทะ พอกระทะร้อนได้ที่ ตักแป้งใส่กะลามะพร้าวหรือขันหรือกระป๋องที่ทำขึ้นอย่างประณีตสำหรับโรยแป้งโดยเฉพาะคือเจาะก้นเป็นรูเล็กๆ จนพรุน\r\n•	วิธีการโรยก็วนไปวนมาให้ทั่วกระทะ หลายๆ ครั้ง จนได้แผ่นขนาดใหญ่ตามต้องการ สุกแล้วใช้ไม้ปลายแหลมพับตลบนำมาวางซ้อนๆ กันให้สะเด็ดน้ำมัน โรยแผ่นใหม่ต่อไป อย่าลืมทาน้ำมันผสมไข่แดงทุกครั้งไป\r\n•	ขนมลาให้โปรตีนจากแป้ง น้ำตาล และไข่แดง และมีส่วนประกอบของไขมันอยู่ด้วย เป็นขนมที่แสดงถึงฝีมือประณีตบรรจงอย่างยิ่ง จากแป้งข้าวเจ้า ผสม น้ำผึ้ง แล้วค่อยๆ ละเลงลงบนกระทะน้ำมันที่ร้อนระอุ กลายเป็นแผ่นขนมลาที่มีเส้นเล็กบางราวใยไหม สอดสานกันเป็นร่างแห\r\n', '1.ข้าวเจ้า\r\n2.	น้ำตาลทราย\r\n3.	น้ำผึ้ง\r\n4.	น้ำมันมะพร้าวใหม่ๆ (หรือน้ำมันอื่นๆ)\r\n5.	ไข่ต้ม (ใช้เฉพาะไข่แดง)\r\n', 'Ds1'),
('f072', 'ขนมเจาะหู', 'C03', 'r4', '1. แป้งข้าวเจ้ากับแป้งข้าวเหนียวผสมกันพักไว้\r\n\r\n2. ผสมน้ำตาลทั้งสองชนิดเข้าด้วยกัน ยกขึ้นตั้งไฟ เติมน้ำเปล่าลงไป เคี่ยวให้น้ำที่ได้มีลักษณะเหนียวข้น ระวังอย่าให้ตกทราย\r\n\r\n3. น้ำตาลที่เคี่ยวได้ทิ้งไว้ให้เริ่มอุ่น ใส่แป้งที่ผสมรวมกันไว้ลงไปนวด นวดประมาณ 30 นาที ให้มีลักษณะขึ้นเงา ทิ้งแป้งไว้ 1 คืน\r\n\r\n4. นำแป้งที่ได้ปั้นเป็นก้อนกลมๆ ให้มีขนาดเท่าๆกัน เตรียมใบตอง (วิธีแบบดั้งเดิม) ทาน้ำมันลงไปเล็กน้อยบนใบตอง\r\n\r\n5. ปั้นแป้งที่ได้จนหมด คลึงบนใบตองกดให้แบน แล้วทำเป็นรูตรงกลาง ลักษณะจะคล้ายการเจาะหู\r\n\r\n6. นำลงทอดให้เหลืองกรอบ ลักษณะจะคล้ายกับโดนัทเล็กๆ ของฝรั่ง\r\n', '1. แป้งข้าวเหนียว 1 ช้อนโต๊ะ\r\n2. แป้งข้าวเจ้า 250 กรัม\r\n3. น้ำตาลโตนด 80 กรัม\r\n4. น้ำตาลทราย 200 กรัม\r\n5. น้ำเปล่า ½ ถ้วย\r\n', 'Ds2');
INSERT INTO `food` (`id`, `name`, `type_id`, `region_id`, `solution`, `ingredient`, `img`) VALUES
('f073', 'ขนมไข่ปลา', 'C01', 'r4', '1. ทำน้ำเชื่อมไว้ก่อน โดยต้มน้ำใส่ใบเตยลงไป ค่อยๆ ใส่น้ำตาลลงไปคนให้ละลายตั้งไฟอ่อนมากๆไว้\r\n\r\n2. ผสมแป้งเพื่อทำตัวขนม แป้งข้าวเจ้าและแป้งข้าวเหนียว เทใส่ในกะละมัง ใส่เนื้อลุกตาลยีลงไป\r\n\r\n3. นวดผสมให้เป็นเนื้อเดียวกัน ถ้าแห้งเกินไป ค่อยๆเติมน้ำนะค่ะ อย่าเติมที่เดียวเยอะ จะแฉะเกินไป\r\n\r\n4. จากนั้นปั้นเป็นเส้นๆ ลักษณะเหมือนไข่ปลา เอาปลายมาทับกันไว้ เรียงใส่ถาดรอต้มไว้\r\n\r\n6.	น้ำเชื่อมที่ได้แบ่งใส่กะละมังอีกใบไว้สำหรับแช่ขนมไข่ปลาที่ต้มสุกแล้ว เอาขนมไส้ปลาที่ปั้นไว้ลงต้มในน้ำเชื่อมที่เหลือ ต้มจนสุกขนมไข่ปลาที่สุกแล้วจะลอยขึ้นมา ช้อนมาใส่ในน้ำเอมที่แบ่งไว้\r\n7.	เวลาจะกินก็เตรียมมะพร้าวคลุกกับเกลือสำหรับคลุก ช้อนเอาแต่เนื้อขนมไข่ปลา เอามาคลุกกับมะพร้าวจัดใส่จาน\r\n', '1. แป้งข้าวเจ้า 100 กรัม\r\n2. แป้งข้าวเหนียว 500 กรัม\r\n3. น้ำตาลทรายทำน้ำเชื่อม 500 กรัม\r\n4. เนื้อลูกตาลยี 500 กรัม\r\n5. มะพร้าวทึนทึกขูดเส้น\r\n6. เกลือป่น เล็กน้อย\r\n7. ใบเตยต้มใส่ในน้ำเชื่อม 5 - 10 ใบ\r\n', 'Ds3'),
('f074', '', 'C13', 'r4', '1. ข้าวสารเหนียวและถั่วดำแช่น้ำไว้ประมาณ 1 ชม. และพักให้สะเด็ดน้ำ\r\n2. ต้มถั่วดำให้สุก แล้วผัดข้าวเหนียวด้วยน้ำกะทิ โดยใส่น้ำกะทิพอท่วมข้าวเหนียวใส่เกลือ ผัดจนแห้งแล้วใส่ถั่วลงไป แม่ห่านทำพลาดในจุดนี้ค่ะ ผัดข้าวเหนียวไปพร้อมกับถั่วดำ ทำให้ข้าวเหนียวที่ได้สีไม่สวย ออกทำไปเลยและต้องใช้เวลานึ่งนานในตอนหลัง\r\n3. ผัดข้าวเหนียวจนแห้งแล้วพักไว้\r\n', '1. ยอดกะพ้อ\r\n2. ข้าวสารเหนียว 1/2 กก.\r\n3. ถั่วดำ/ถั่วแดง 100 กรัม\r\n4. น้ำกะทิ 1/2 กก.\r\n5. เกลือ 1 ช้อนโต๊ะ\r\n', 'Ds4'),
('f075', 'ขนมจู้จุน', 'C03', 'r4', '๑. นำ น้ำตาลทรายและน้ำตาลปีปผสมกัน เคี่ยวจนเป็นน้ำตาลข้น\r\n๒.นำแป้งใส่ชามผสมนำน้ำตาลที่เคี่ยวใส่ผสมลงไป\r\n๓. คนให้เข้ากัน\r\n๔. ผสมแป้งเชื้อลงคนให้เข้ากัน ประมาณ ๑๐ นาที\r\n๕.นำไปทอดในน้ำมันที่เดือด\r\n๖.พอเหลืองสุกนำขึ้นมาพักค้างไว้ให้เสด็ดน้ำมัน ประมาณ ๓ นาที นำมารับประทานได้\r\n', '๑. แป้งข้าวเจ้า ๑ กก.\r\n๒. แป้งข้าวเหนียว ๑.๕ กก.\r\n๓. น้ำตาลปีป ๐.๕ กก.\r\n๔. เชื้อแป้งหมัก\r\n๕. เกลือ ๑ ช้อนชา\r\n๖. น้ำมันพืช ๑ กก.\r\n๗. น้ำตาลทรายขาว๐.๕ กก.\r\n', 'Ds5'),
('f076', 'ขนมไข่', 'C14', 'r4', '1. ตีไข่ให้ขึ้นฟู แล้วเติมน้ำตาลทีละน้อยจนหมด ตีต่อไปจนน้ำตาลตั้งยอดอ่อน\r\n\r\n2. ร่อนแป้งผงฟูเข้าด้วยกัน แล้วแบ่งออกเป็น 2 ส่วน ใส่แป้งส่วนที่ 1 ลงในไข่ที่ตีแล้ว เคล้าเบาๆให้เข้ากัน ใส่น้ำมะนาวลงไป ใส่แป้งส่วนที่ 2 เคล้าเบาๆอีกครั้ง\r\n\r\n3. ทาน้ำมันพืชที่พิมพ์ขนมอบให้ร้อน แล้วใส่แป้งที่ผสมลงในพิมพ์ปิดฝาอบให้แป้งสุกแห้งจนขนมเป็นสีทอง แกะขึ้นใส่ถาด พักไว้ให้เย็น\r\n', '- ไข่ขาวของไข่ไก่ 1 ถ้วยตวง\r\n\r\n- แป้งเค้ก 1 ถ้วยตวง\r\n\r\n- น้ำตาลทราย 1 ถ้วยตวง\r\n\r\n- น้ำมะนาว 1 ช้อนชา\r\n\r\n\r\n- ผงฟู 1 ช้อนชา\r\n', 'Ds6'),
('f077', 'ขนมพอง', 'C03', 'r4', '1. แช่ข้าวสารเหนียวทิ้งไว้ 1 คืนแล้ว\r\n\r\n2. นำมาล้างให้สะอาดจนหมดกลิ่น จากนั้นนำไปนึ่งด้วยสวด(เครื่องปั้นดินเผาปากกว้างก้นเล็กเจาะรูหลายรูให้ ไอน้ำเข้าด้านล่างออกด้านบน)\r\n\r\n3. เมื่อสุกแล้วนำมาอัดลงในแบบพิมพ์เป็นรูปตามต้อง การ(แบบพิมพ์มักทำด้วยไม้ไผ่แผ่นบางๆเป็นขอบสูงประมาณ๑เซนติเมตร)โดยมากทำ เป็นรูปวงกลมรูปพระจันทร์ครึ่งซีกรูปสามเหลี่ยมรูปข้าวหลามตัดและรูปทรงพุ่ม ข้าวบิณฑ์เมื่อตกแต่งข้าวเหนียวเป็นรูปตามแบบพิมพ์แล้วจะถอดพิมพ์ออก\r\n\r\n4. เมื่อทำเสร็จแต่ละชิ้นนำเรียงไว้บนกระดังหรือภาชนะอื่นๆแล้วนำไปตากแดดจนแห้ง\r\n\r\n5. นำไปทอดในกระทะที่น้ำมันกำลังร้อนจัด(ใช้น้ำมันมะพร้าวใหม่หรือน้ำมันอื่นๆ)ข้าวเหนียวก็พองฟูขึ้น\r\n\r\n6. เมื่อสุกดีแล้วก็ตักใส่ตะแกรงให้สะเด็ดน้ำมัน โดยปกติพองจะเป็นสีขาวแต่ถ้าต้องการให้เป็นสีอื่นก็ใช้สีที่ต้องการย้อมข้าว เหนียวตั้งแต่ตอนแช่น้ำ\r\n', '1.สารข้าวเหนียว\r\n2.น้ำมันพืช\r\n', 'Ds7'),
('f078', 'ขนมโค', 'C01', 'r4', '1)ผสมแป้งกับน้ำร้อนที่กำลังเดือด เทใส่ลงในแป้งแล้วนวดให้แป้ง เข้ากัน ดี พอปั้นได้ แล้วพักแป้งไว้โดยใช้ผ้าชุบน้ำหมาดๆ คลุมแป้งพักไว้\r\n2) ปั้นไส้เผือก หรือ กระฉีก ให้เป้นก้อนกลมๆ เล็กๆ ขนาดถ้าปั้นใหญ่ลูกขนมก็จะใหญ่ ก็เอาแบบขนาดกำลัง ลูกละคำแบบพอประมาณแล้วกันเน้อ \r\n3 ) วิธีทำส่วนผสมกะทิ ผสมแป้งข้าวเจ้า ,น้ำตาลทราย,เกลือ คนให้ละลาย แล้วนำไปตั้งไฟอ่อน ต้องหมั่นคนตลอดเวลา เพื่อไม่ให้แป้งเป็นก้อน พอเดือดก็ยกลง\r\n4)นำตัวแป้งที่นวดไว้ ปั้นเป็นก้อนกลม เอาไปหุ้มไส้ไว้ให้มิด คลึงให้เป้นก้อนกลม แล้วนำไปต้มในน้ำเดีอดที่ ใส่ใบเตยด้วย พอแป้งลอบขึ้น ตักใส่น้ำเย็น ที่สะอาด เพื่อไม่ให้แป้งยึดติดกัน แล้วตักขึ้นใส่กระชอน แล้วก็ไปใส่รวมกับ ส่วนผสมของ กะทิที่เราทำไว้แล้ว แค่นั้นเอง ก็รับประทานได้ทันที\r\n', '1) แป้งข้าวเหนียว 1 ถ้วย( แล้วแต่ปริมาณมากน้อย) \r\n2) น้ำร้อนเดือด 1/2 ถ้วย\r\n3) น้ำกะทิ 1-1/2 ถ้วย ,แป้งข้าวเจ้า 1 ช้อนโต๊ะ.เกลือ นิดหน่อย\r\n4) ใบเตย ไว้ใส่น้ำต้ม ขนม\r\n', 'Ds8'),
('f079', 'น้ำพริกตาแดง', 'C05', 'r1', '1.	นำ หอมแดง และกระเทียมไปย่างไฟ และนำใบตองห่อ ปลาร้า และกะปิ ย่างไฟ\r\n2.	เตรียมครก โขรก พริกแห้ง หอมแดง กระเทียม กะปิ ปลาร้า และเนื้อปลาย่าง ให้ละเอียดเข้ากัน\r\n3.	เสริฟน้ำพริกทานกับผักสด กับข้าว อาหารเหนือ เมนูสุขภาพ\r\n', '	พริกแห้ง 8 เม็ด\r\n	หอมแดง 6 หัว\r\n	กระเทียม 4 หัว\r\n	กะปิ 1 ช้อนชา\r\n	เนื้อปลาร้าสับ 1 ช้อนโต๊ะ\r\n	เนื้อปลาย่าง 4 ช้อนโต๊ะ\r\n', 'Fn2'),
('f080', 'แกงคั่วฟักใส่ไก่ ', 'C04', 'r1', '1.	ตั้งหม้อ ทำการต้มกะทิให้เดือด จากนั้นใส่พริกแกงและฟักเขียวลงไปต้ม\r\n2.	ปรุงรสด้วย น้ำตาล น้ำปลา และน้ำมะขามเปียก ปรุงจนได้รสที่พอใจ\r\n3.	ใส่ไก่และใบมะกรูดลงไป พอไก่สุกใส่ใบโหระพาลงไป พร้อมเสริฟรับประทานกับข้าวง่ายๆ แกงกะทิ\r\n', '	ฟักเขียวหั่นเป็นชิ้น 1 ถ้วย\r\n	เนื้อไก่หั่นพอคำ 1 ถ้วย\r\n	หัวกะทิ 1 ถ้วย\r\n	หางกะทิ 1 ถ้วย\r\n	พริกแกงเผ็ด 1 ช้อนชา\r\n	น้ำตาลปี๊บ 2 ช้อนโต้ะ\r\n	น้ำปลา 2 ช้อนโต้ะ\r\n	น้ำมะขามเปี๊ยก 2 ช้อนโต้ะ\r\n	ใบโหระพา 1 กำมือ\r\n	ใบมะกรูดหั่นฝอย 2 ช้อนโต้ะ\r\n', 'Fn3'),
('f081', 'ขาหมูต้มผักกาดดอง ', 'C01', 'r1', '1.	ต้มขาหมูในน้ำซุป เคี่ยวจนเนื้อขาหมูนุ่ม ใช้เวลาประมาณ 45-60 นาที\r\n2.	นำผักกาดดองลงไปต้ม ปรุงรสด้วย น้ำตาล น้ำปลา พริกไทย และซอสถั่วเหลือง\r\n3.	ต้มต่อให้รสชาดของผักกาดดองออกรสชาติ เสริฟใส่ชาม กับข้าวง่ายๆ เมนูขาหมู\r\n', '	ขาหมูหั่นเป็นชิ้น 1 ถ้วย\r\n	น้ำซุปหมู 1 หม้อ\r\n	ผักกาดดองหั่นเป็นชิ้น 1 ต้น\r\n	น้ำตาล 1/2 ช้อนโต้ะ\r\n	น้ำปลา 2 ช้อนโต้ะ\r\n	ซอสถั่วเหลือง 1 ช้อนโต้ะ\r\n	พริกไทย 1 ช้อนชา\r\n', 'Fn4'),
('f082', 'น้ำพริกอ่องปลากระป๋อง  ', 'C05', 'r1', '1.	เตรียมครก จากนั้นนำ พริกแห้ง เกลือ หอมแดง กระเทียม รากผักชีบดและกะปิ ลงไปโขรกให้ละเอียดเป็นเนื้อเดียวกัน\r\n2.	ตั้งกระทะน้ำมันให้ร้อนนำส่วนผสมที่โขรกไว้ลงไปผัดในกระทะร่วมกับเนื้อปลากระป๋องและน้ำซอสปลากระป๋อง\r\n3.	ปรุงรสด้วย น้ำตาล น้ำปลา และนำมะเขือเทศลงไปผัดให้เข้ากัน\r\n4.	เสริฟน้ำพริกอ่องปลากระป๋อง ทานร่วมกับผักสด\r\n', '	เนื้อปลากระป๋อง 1 กระป๋อง(นำมาสับและเอาพร้อมกับน้ำซอสด้วย)\r\n	พริกแห้งเม็ดใหญ่ 5 เม็ด (แช่น้ำและเอาแต่เปลือก)\r\n	หอมหัวแดง 4 หัว(นำไปเผาไฟให้หอม)\r\n	กระเทียม 2 หัว (นำไปเผาไฟให้หอม)\r\n	รากผักชีบดละเอียด 2 ช้อนโต้ะ\r\n	กะปิ 1 ช้อนชา(นำมาห่อใบตองและเผาไฟให้หอม)\r\n	เกลือ 1 ช้อนชา\r\n	มะเขือเทศผ่าครึ่ง 4-5 ผล\r\n	น้ำปลา 2 ช้อนโต๊ะ\r\n	น้ำตาล 2 ช้อนโต๊ะ\r\n	น้ำมันพืช 2 ช้อนโต้ะ\r\n	ใบต้นหอมซอย 1 ช้อนชา\r\n	ผักสดสำหรับทานกับน้ำพริก เช่น แตงกวา ผักสลัด มะเขือ  เป็นต้น\r\n', 'Fn5'),
('f083', 'แกงปลาใส่ตูน ', 'C04', 'r1', '1.	เตรียมเครื่องแกง โดย โขลก พริก หอมหัวแดง กระเทียม กะปิ ปลาร้า และขมิ้น ให้ละเอียดเป็นเนื้อเดียวกัน\r\n2.	ตั้งกระทะน้ำมัน นำเครื่องแกงลงไปผัด ให้สุก จากนั้นเติมน้ำซุปลงไป\r\n3.	เมื่อน้ำเดือดจัด ให้ใส่ปลาลงไปต้ม ตามด้วย ตูน ใบโหระพา มะเขือเทศ\r\n4.	ลดความร้อน ปรุงรสด้วย เกลือ น้ำปลา น้ำมะนาว และน้ำมะกรูด\r\n5.	เสริฟใส่ชามพร้อมรับประทาน กับข้าวง่ายๆ เมนูแกงเหนือ\r\n', '	ปลาช่อน หั่นเป็นชิ้น 1 ถ้วย\r\n	ตูนหั่นเป็นท่อน 2 ถ้วย\r\n	น้ำมะนาว 1 ช้อนโต้ะ\r\n	น้ำมะกรูด 1 ช้อนโต๊ะ\r\n	ใบโหระพา 1 กำมือ\r\n	มะเขือเทศ หั่นครึ่ง 5 ลูก\r\n	น้ำปลา 1 ช้อนโต้ะ\r\n	เกลือ 1 ช้อนโต๊ะ\r\n	น้ำซุป 4 ถ้วย\r\n	น้ำมันพืช 2 ช้อนโต้ะ\r\n	พริกสด 10 เม็ด\r\n	หอมหัวแดง 5 หัว\r\n	กระเทียม 2 หัว\r\n	กะปิ 1 ช้อนโต๊ะ\r\n	ปลาร้าสับ 1 ช้อนโต๊ะ\r\n	ขมิ้น 2 ช้อนชา\r\n', 'Fn6'),
('f084', 'แกงไก่ใส่ฟักหม่น ', 'C03', 'r1', '1.	เตรียมเครื่องแกง โดยโขลก พริกแห้ง ข่า ตะไคร้ หอมหัวแดง กระเทียม กะปิ และปลาร้าสับ ให้ละเอียดเป็นเนื้อเดียวกัน\r\n2.	ต้มน้ำซุป จากนั้นนำ เนื้อไก่ และเครื่องแกงลงไปต้ม ปรุงรสด้วย เกลือและน้ำปลา\r\n3.	ใส่ฟักเขียว และมะแขว่น ลงไป รอจนฟักสุก ใส่ผักชีฝรั่งลงไป\r\n4.	เสริฟใส่ชามพร้อมรับประทาน กับข้าวไทย เมนูไก่ แกงเหนือ\r\n', '	เนื้อไก่ หั่นเป็นชิ้น 1 ถ้วย\r\n	ฟักเขียวหั่นเป็นชิ้น 2 ถ้วย\r\n	ผักชีฝรั่ง ซอย 2 ช้อนโต้ะ\r\n	มะแขว่น โขลกให้ละเอียด 2 ช้อนโต๊ะ\r\n	น้ำปลา 1 ช้อนโต๊ะ\r\n	น้ำมันพืช 3 ช้อนโต๊ะ\r\n	น้ำซุป 3 ถ้วย\r\n	พริกแห้ง 4 เม็ด\r\n	ข่าซอย 1 ช้อนโต้ะ\r\n	ตะไคร้ซอย 3 ช้อนโต๊ะ\r\n	หอมหัวแดง 3 หัว\r\n	กระเทียม 2 หัว\r\n	เกลือ 1 ช้อนชา\r\n	ปลาร้าสับ 1 ช้อนโต๊ะ\r\n	กะปิ 1 ช้อนชา\r\n', 'Fn7'),
('f085', 'แกงผักหวานใส่ปลาย่าง', 'C04', 'r1', '1.	เตรียมเครื่องแกงโดย โขรก พริกแห้ง หอมแดง กระเทียม กะปิและปลาร้าสับให้ละเอียดเป็นเนื้อเดียวกัน\r\n2.	ต้มน้ำซุปให้เดือด นำเครื่องแกงลงไปต้ม ตามด้วยเนื้อปลาย่าง มะเขือเทศ นำเครื่องแกงทั้งหมดมาโขลกให้ละเอียด 2 ปลากรอบ นำไปต้มกับน้ำซุป ใส่เครื่องแกง\r\n3.	ใส่ผักหวาน ต้มจนสุก ใส่วุ้นเส้นและมะเขือเทศ 4 ชิมดูอ่อนเค็ม เติมเกลือตามชอบ กับข้าวง่ายๆ\r\n\r\n', 'เนื้อปลาย่าง 1 ถ้วย\r\n	ผักหวาน 2 ถ้วย\r\n	มะเขือเทศผ่าครึ่ง 4 ลูก\r\n	วุ้นเส้น 1 ถ้วย\r\n	น้ำซุป 2 ถ้วย\r\n	พริกแห้ง 4 เม็ด\r\n	หอมแดง 3 หัว\r\n	กระเทียม 2 หัว\r\n	กะปิ 3 ช้อนชา\r\n	ปลาร้าสับ 2 ช้อนชา\r\n', 'Fn8'),
('f086', 'แกงขนุนใส่ซี่โครงหมู ', 'C04', 'r1', '1.	เตรียมเครื่องแกงโดย โขลก พริกแห้ง หอมแดง กระเทียม กะปิ และ ปลาร้า ให้ละเอียดเป็นเนื้อเดียวกัน\r\n2.	ต้มน้ำให้เดือน นำเครื่องแกงลงไปต้ม และใส่ซี่โครงหมูลงไปให้สุก\r\n3.	ใส่มะเขือลงไป ผักชีฝรั่ง ยอดชะอม และใบชะพลู ต้มจนสุกและเสริฟใส่ชามพร้อมรับประทาน กับข้าวง่ายๆ\r\n', '       ซี่โครงหมูอ่อน 1 ถ้วย\r\n	เนื้อขนุนอ่อน หั่นเป็นชิ้น 1 ถ้วย\r\n	ยอดชะอมอ่อน ครึ่งถ้วย\r\n	มะเขือเทศ ผ่าครึ่ง ครึ่งถ้วย\r\n	ผักชีฝรั่งซอยหยาบ 5 ช้อนโต้ะ\r\n	ใบชะพลูซอย 5 ช้อนโต้ะ\r\n	พริกแห้ง 4 เม็ด\r\n	หอมแดง 3 หัว\r\n	กระเทียม 2 หัว\r\n	กะปิ 3 ช้อนชา\r\n	ปลาร้าสับ 2 ช้อนชา\r\n	น้ำเปล่า 2 ถ้วย\r\n', 'Fn9'),
('f087', 'แกงคั่วพริกซี่โครงหมูอ่อน', 'C04', 'r4', '1.	เตรียมเครื่องแกง ด้วยการ โขรก พริกกะเหรี่ยง พริกสด กระเทียม หอมแดง ตะไคร้ ข่า พริกไทยแห้ง และขมิ้นใต้ โขรกให้ละเอียดพอหยาบๆ จากนั้นพักเอาไว้ก่อน\r\n2.	ต้มซี่โครงหมูในน้ำซุป และใส่กะปิลงไปด้วย ต้มซี่โครงหมูจนน้ำซุปง่วน และเนื้อซี่โครงหมูอ่อนนุ่ม\r\n3.	เมื่อน้ำซุปเริ่มงวด แล้วให้ใส่พริกแกงลงไปผัด สัก 3 ช้อนโต้ะ\r\n4.	ปรุงรสด้วยน้ำตาลและเกลือ เสริฟแกงคั่วพริกซี่โครงหมูอ่อน ทานกับข้าวหอมมะลิร้อนๆ\r\n', '●	หมูอ่อนหั่นเป็นชิ้นพอคำ 1 จาน\r\n●	น้ำซุปหมู 4 ถ้วย\r\n●	กะปิ 2 ช้อนโต้ะ\r\n●	น้ำตาล 1 1/2 ช้อนโต้ะ\r\n●	เกลือ 1/2 ช้อนชา\r\n●	พริกกะเหรียง 2 ช้อนโต้ะ\r\n●	ตะไคร้สับ 2 ช้อนโต้ะ\r\n●	กระเทียม 2 ช้อนโต้ะ\r\n●	ข่าซอย 2 ช้อนโต้ะ\r\n●	พริกไทยแห้ง 1 ช้อนโต้ะ\r\n●	พริกขี้หนูสด 2 ช้อนโต้ะ\r\n●	ขมิ้นใต้ 2 ช้อนโต้ะ\r\n', 'Fs1'),
('f088', 'ขนมจีนน้ำพริกกุ้งสด', 'C05', 'r4', '1. ต้มน้ำซุปให้เดือด จากนั้นนำกุ้งไปลวกให้สุก จากนั้นนำกุ้งออกมาพักให้เย็น นำเนื้อกุ้งมาสับให้หยาบๆ\r\n2. กรองน้ำซุปใส่ชาม ใส่กะปิ น้ำตาลปี๊บ น้ำมะนาว ผสมให้ส่วนผสมเข้ากัน จากนั้นใส่ เนื้อกุ้ง หอมแดง และพริกสดลงไปผสมให้เข้ากัน\r\n3. เสริฟขนมจีนพร้อมน้ำพริกกุ้งสด ทานคู่กับเครื่องเคียงตามต้องการ\r\n', 'ขนมจีน 1 จาน\r\n2.	กะปิ 2 ช้อนโต้ะ\r\n3.	น้ำตาลปี๊บ 2 ช้อนโต้ะ\r\n4.	กุ้ง 5-10 ตัว\r\n5.	น้ำซุปหมู 1 ถ้วย\r\n6.	พริกสดซอย 4 ช้อนโต้ะ\r\n7.	หอมแดงซอย 4 ช้อนโต้ะ\r\n8.	น้ำมะนาว 3 ช้อนโต้ะ\r\n9.	ผักและอาหารสำหรับทานกับขนมจีน เช่น ไข่ต้ม ผักสดต่างๆ\r\n', 'Fs10'),
('f089', 'ปลาดุกหมกกะปิ', 'C03', 'r4', '1.	ปลาดุก 1 ตัว (นำมาหั่นเป็นท่อนล้างให้สะอาดด้วยน้ำเกลือ)\r\n2.	กะปิ 2 ช้อนโต้ะ\r\n3.	กระเทียมบด 4 ช้อนโต้ะ\r\n4.	พริกไทยบด 2 ช้อนโต้ะ\r\n5.	น้ำตาล 4 ช้อนโต้ะ\r\n6.	น้ำปลา 2 ช้อนโต้ะ\r\n7.	น้ำมันสำหรับทอดปลา\r\n', '1. หลังจากล้างปลาดุกให้สะอาดแล้ว ให้นำปลาดุกพักใส่จานไว้ก่อน\r\n2. ปรุงรสกะปิ โดย นำกระเทียม พริกไทย น้ำตาล น้ำปลา และกะปิในอัตราส่วนที่กำหนดไว้ข้างต้น ผสมให้ส่วนผสมเข้ากัน\r\n3. กะปิปรุงรสมาคลุกกับปลาดุก อย่าหมักไว้นาน ให้ตั้งน้ำมันให้ความร้อนปานกลาง นำปลาดุกลงไปทอดเลย ให้เหลืองสุก\r\n4. เสริฟปลาดุกหมกกะปิ ทานกับข้าวสวยร้อนๆสุดแสนอร่อย\r\n', 'Fs11'),
('f090', 'ผัดพริกแกงหอยแครงใบยี่หร่า', 'C02', 'r4', '1. ต้มน้ำซุปให้เดือน จากนั้นใส่พริกแกงใต้ ลงไปต้ม\r\n2. ปรุงรสด้วย กะปิ น้ำตาลปี๊บ และน้ำปลา ชิมรสตามใจชอบ จากนั้นใส่ หอยแครงและใบยี่หร่าลงไปต้ม\r\n3. เคี่ยวให้หอยสุก และน้ำพร่องลงไปเหลือขลุกขลิก เสริฟใส่จานพร้อมรับประทาน\r\n', '1.     พริกแกงใต้ 3 ช้อนโต้ะน้ำซุป 2 ถ้วย\r\n2.	น้ำตาลปี๊บ 1 ช้อนโต้ะ\r\n3.	กะปิ 1 ช้อนโต้ะน้ำปลา 1 ช้อนโต้ะ\r\n4.	หอยแครง 1 ถ้วย\r\n5.	ใบยี่หร่า 1 กำมือ\r\n', 'Fs12'),
('f091', 'ผัดสุกี้ยากี้ทะเลแห้ง', 'C02', 'r4', '1. ตั้งกระทะน้ำมันให้ร้อน จากนั้นตอกไข่ลงไปผัด ตามด้วย กุ้ง ปลาหมึก ผัดให้สุก\r\n2. จากนั้นใส่ ผัก ทั้งหมดและวุ้นเส้นลงไปผัด และเติมน้ำจิ้มสุกี้ลงไปผัดด้วย\r\n3.ผัดให้ส่วนผสมเข้ากัน จากนั้นเสริฟใส่จาน โรยหน้าด้วยเนื้อปลาลวก สาเหตุที่ใส่เนื้อปลาลวกสุดท้ายเพราะว่าถ้านำเนื้อปลาลงไปผัดพร้อมกับส่วนผสมอื่นเนื้อปลาจะเละไม่น่ารับประทาน\r\n', 'น้ำจิ้มสุกี้ 1 ถ้วยน้ำจิ้ม \r\nผักบุ้งหั่นเป็นชิ้นพอคำ 1 ถ้วย\r\nผักกาดขาวหั่นเป็นชิ้นพอคำ 1 ถ้วย\r\nวุ้นเส้นแช่น้ำ 1 ถ้วย\r\nผักคื่นฉ่าย หั่นเป็นชิ้นพอคำ 1 ต้น\r\nต้นหอมหั่นเป็นชิ้นพอคำ 1 ต้น\r\nไข่ไก่ 1 ฟอง\r\nปลาหมึกหั่นเป็นชิ้นพอคำ 1 ตัว\r\nกุ้งสด 5-6 ตัว\r\nเนื้อปลาลวก 5-6 ชิ้น\r\nน้ำมัน 2 ช้อนโต้ะ\r\n', 'Fs13'),
('f092', 'ไก่กะปิ', 'C02', 'r4', '1.	ตั้งกระทะน้ำมันให้ร้อน ผัดกระเทียมให้หอม จากนั้นใส่เนื้อไก่ลงไปผัดให้สุก\r\n2.	ปรุงรสด้วย กะปิ น้ำปลา และน้ำตาลปี๊บ ผัดให้ส่วนผสมเข้ากัน ปรุงรสตามใจชอบ\r\n3.	นำใบโหระพา ใบมะกรูดและพริกสดลงไปผัดให้หอม ก็พร้อมเสริฟรับประทาน \r\n', '	เนื้อไก่หั่นเป็นชิ้นพอคำ 1 ถ้วย\r\n	กะปิ 1 ช้อนโต้ะ\r\n	กระเทียมบด 1 ช้อนโต้ะ\r\n	พริกสดซอย 1 ช้อนโต้ะ\r\n	ใบมะกรูดซอย 1 ช้อนชา\r\n	ใบโหระพา 2 ช้อนโต้ะ\r\n	น้ำปลา 1 ช้อนโต้ะ\r\n	น้ำตาลปี๊บ 1 ช้อนโต้ะ\r\n	น้ำมันพืช 3 ช้อนโต้ะ\r\n', 'Fs14'),
('f093', 'ข้าวหมกไก่', 'C13', 'r4', '1.	ตั้งกระทะน้ำมัน ให้ร้อนจากนั้นนำหอมแดงมาเจียวให้เหลือง และพักไว้สำหรับโรยหน้าข้าวหมกไก่\r\n2.	นำเม็ดผักชี ลูกยี่หร่า พริกไทย อบเชย การพลู ลูกกระวาน มาคั่วให้หอม จากนั้นนำส่วนผสมทั้งหมด พร้อมกับ พริกสด เกลือ ผงขมิ้น และผงกะหรี่มาโขรกให้ละเอียด\r\n3.	จากนั้นนำเครื่องเทศที่โขรกมาผสมกับนมสดและนำหมักกับน่องไก่\r\n4.	ซาวข้าวด้วยน้ำ จากนั้นนำข้าวสารไปผสมกับเครื่องหมักน่องไก่ จากนั้นนำไปหุงในหม้อหุงข้าวเติมน้ำพอประมาณ หุงพร้อมกับน่องไก่เลย\r\n5.	เมื่อข้าวสุกก็เสริฟข้าวหมกพร้อมน่องไก่ โรยหน้าด้วยหอมเจียว\r\n', '	ข้าวสาร 3 ถ้วย\r\n	น่องไก่ 1 ถ้วย\r\n	เม็ดผักชี 1 ช้อนโต้ะ\r\n	ลูกยี่หร่า 1 ช้อนชา\r\n	อบเชย 1 แท่ง\r\n	กานพลู 4 ดอก\r\n	พริกสด 3 เม็ด\r\n	เกลือ 1 ช้อนโต้ะ\r\n	ผงขมิ้น 1 ช้อนชา\r\n	ผงกระหรี่ 1 ช้อนโต้ะ\r\n	นมจืด ครึ่งถ้วยหอมแดงซอย 1 ถ้วยตวง\r\n', 'Fs15'),
('f094', 'ขนมจีนน้ำยาปู', 'C04', 'r4', '1.	เตรียมเครื่องแกง โดย โขรก พริกแห้ง กะปิ ข่า ตะไคร้ ขิง ขมิ้น ผิวมะกรูด พริกไทยและกระเทียม ให้ละเอียดเป็นเนื้อเดียวกัน\r\n2.	ตั้งหม้อต้ม นำกะทิลงไปต้ม จากนั้น นำเครื่องแกงลงไปต้มให้แตกมัน จากนั้นใส่เนื้อปูลงไป ปรุงรสด้วย น้ำตาล เกลือ ต้มให้เนื้อปูเปลื่อย\r\n3.	ใส่ใบมะกรูดลงไปต้มให้หอม เสริฟใส่ถ้วย ทานคู่กับขนมจีน\r\n', '	เนื้อปู 1 ถ้วย\r\n	กะทิ 2 ถ้วย\r\n	น้ำตาลปี๊บ 1 ช้อนโต้ะ\r\n	เกลือ 1 ช้อนโต๊ะ\r\n	ใบมะกรูดซอย 1 ช้อนโต้ะ\r\n	พริกขี้หนูแห้ง 20 เม็ด\r\n	พริกแห้งเม็ดใหญ่ 5 เม็ด\r\n	พริกไทยดำ 1 ช้อนชา\r\n	ข่าซอย 1 ช้อนโต๊ะ\r\n	ตะไคร้ซอย 3 ช้อนโต้ะ\r\n	ผิวมะกรูด 1 ช้อนชา\r\n	กระเทียมสด 4 กลีบ\r\n	ขมิ้นสดซอย 1 ช้อนโต๊ะ\r\n	กะปิ 1 ช้อนโต้ะ\r\n	ขนมจีน\r\n', 'Fs16'),
('f095', 'ขนมจีนน้ำยากะทิ', 'C01', 'r4', '1.	ต้มน้ำให้เดือด จากนั้นใส่ หอมแดง กระเทียม ข่า ตะไคร้ กระชาย และเนื้อปลาช่อนลงไปต้ม จนสุกเนื้อหอมแดง กระเทียมและกระชายเริ่มอ่อน\r\n2.	จากนั้นแยก หอมแดง กระเทียม กระชาย ข่า ตะไคร้ และเนื้อปลาช่อน มาโขรกรวมกันให้ละเอียด\r\n3.	จากนั้นนำลงไปในหม้อต้มสุกอีกครั้งหนึ่ง จากนั้นปรุงรสด้วย กะปิ เกลือ และเติมลุกชิ้นปลาลงไปต้มด้วย\r\n4.	เดิมกะทิและหัวกะทิลงไป ต้มต่อจนหัวกะทิแตกมัน เสริฟ น้ำยากับขนมจีน พร้อมด้วยเครื่องเคียง\r\n', '	หัวกะทิ 1 ถ้วยตวง\r\n	หอมแดง 1 ถ้วย\r\n	กระเทียม 0.5 ถ้วย\r\n	ข่าหั้น 0.5 ถ้วย\r\n	ตะไคร้หั่น 0.5 ถ้วย\r\n	กระชาย 1 ถ้วย\r\n	หางกะทิ 4 ถ้วยตวง\r\n	เนื้อปลาช่อน 1 ถ้วย\r\n	ลูกชิ้นปลา 1 ถ้วย\r\n	น้ำปลา 2 ช้อนโต๊ะ\r\n	น้ำ 1 หม้อ 5 ลิตร\r\n	เกลือ 1 ช้อนชา\r\n	กะปิ 1 ช้อนชา\r\n	ขนมจีน\r\n	เครื่องเคียงสำหรับทานกับขนมจีนน้ำยากะทิ \r\n', 'Fs17'),
('f096', 'ขนมจีนน้ำยาป่า', 'C01', 'r4', '1.	ต้มน้ำให้เดือด จากนั้นนำ ปลา กระชาย หอมแดง กระเทียม ลงไปต้มให้สุกและนิ่ม\r\n2.	แยกปลาจากหม้อ แยกเอาแต่เนื้อ จากนั้นนำมาโขรกให้ละเอียด นำเนื้อปลาลงไปต้ม\r\n3.	นำกระชาย หอมแดง และกระเทียม ไปโขรกให้ละเอียดจากนั้นนำลงไปในหม้อต้ม\r\n4.	ใส่พริก และ ปลาร้าลงไปต้ม จากนั้นปรุงรส ตามใจชอบ\r\n5.	เสริฟ ขนมจีน กับ น้ำยาป่า ทานกับไข่ต้มและผักสด\r\n', '	น้ำปลาร้าต้มสุก 2 ถ้วยตวง\r\n	พริกสด ครึ่งถ้วยตวง\r\n	กระชาย ครึ่งถ้วยตวง\r\n	หอมแดง 1 ถ้วยตวง\r\n	กระเทียม ครึ่งถ้วยตวง\r\n	ปลาช่อน 1 ตัว หรือปลานิล\r\n	ขนมจีน 2 ถ้วย\r\n	ไข่ต้มและผักสดสำหรับทานกับขนมจีนน้ำยา \r\n', 'Fs18'),
('f097', 'ปลาผัดสะตอ', 'C02', 'r4', '1.	โขรก พริก หอมแดงและกะปิให้ละเอียดเข้ากัน\r\n2.	ตั้งกระทะน้ำมัน นำพริก หอมแดง และ กะปิที่โขรกไว้ลงไปผัดให้สักพักจากนั้นนำปลาและสะตอลงไปผัดด้วยเติมน้ำลงไปนิดหน่อย\r\n3.	ปรุงรสด้วย น้ำปลา น้ำตาล มะนาว\r\n4.	เสริฟใส่จานอาหาร  กับข้าวง่ายๆ เมนูปลา\r\n', '	ปลาหั่นเป็นชิ้นๆ 1 ตัว\r\n	เม็ดสะตอ ผ่าครึ่ง 1 ถ้วย\r\n	หอมแดงซอย 3 ช้อนโต้ะ\r\n	พริกสดซอย 2 ช้อนโต้ะ\r\n	กะปิ 1 ช้อนชา\r\n	น้ำปลา 1 ช้อนโต๊ะ\r\n	น้ำตาล 1 ช้อนชา\r\n	น้ำมะนาว 1 ช้อนชา\r\n	น้ำเปล่า 2 ช้อนโต๊ะ\r\n	น้ำมันพืช 2 ช้อนโต้ะ\r\n', 'Fs19'),
('f098', 'แกงคั่วแห้งกุ้งมะพร้าวอ่อนใบชะพลู', 'c04', 'r4', '1.	เตรียมทำเครื่องแกง ด้วยการโขรก เกลือ ข่า ตะไคร้ พริกสด พริกแห้ง กระเทียม พริกไทยเม็ดแห้ง ขมิ้นสวน ผิวมะกรูด และกะปิ โขรกให้ส่วนผสมเข้ากัน นำมาพักไว้ก่อน\r\n2.	ตั้งกระทะ ใส่หัวกะทิลงไปต้ม ให้แตกมัน จากนั้นใส่พริกแกงลงไปผัดให้กลิ่นหอมออก จากนั้นใส่ น้ำตาลปี๊บ เกลือ และน้ำปลาลงไป\r\n3.	ชิมรสตามใจชอบ จากนั้นใส่กุ้งและใบชะพลูลงไปผัดกับเครื่องแกงในกระทะ ไม่ต้องผัดให้กุ้งสุกเกินไป\r\n4.	เสริฟใส่จาน แต่งหน้าด้วยพริกสด ใบมะกรูดซอย และหัวกะทิ เสร็จกับข้าวง่ายๆ เมนูแกงคั่ว\r\n', 'พริกแห้ง 2 ช้อนโต้ะ\r\n●	เกลือ 1 ช้อนชา\r\n●	ข่าซอย 2 ช้อนโต้ะ\r\n●	ตะไคร้ซอย 2 ช้อนโต้ะ\r\n●	พริกสด 2 ช้อนโต้ะ\r\n●	พริกไทยแห้ง 1 ช้อนโต้ะ\r\n●	กระเทียม 1 ช้อนโต้ะ\r\n●	ขมิ้นสวนซอย 1 ช้อนโต้ะ\r\n●	กะปิ 1 ช้อนโต้ะ\r\n●	ผิวมะกรูด 1 ช้อนโต้ะ\r\n●	หัวกะทิ 1 ถ้วยตวง\r\n●	น้ำตาลปี๊บ 1 ช้อนโต้ะ\r\n●	เกลือ 1 ช้อนชา\r\n●	น้ำปลา 1 ช้อนโต้ะ\r\n●	กุ้งแชบ๋วย 8 – 10 ตัว(แกะเปลือกและผ่านหลัง)\r\n●	เนื้อมะพร้าวอ่อน 4 ช้อนโต้ะ\r\n●	ใบชะพลู 5-10 ใบ\r\n', 'Fs2'),
('f099', 'ข้าวผัดปู', 'C02', 'r4', '1.	ตั้งกระทะน้ำมันให้ร้อน นำไข่ลงไปผัดกับเนื้อกุ้ง ผัดจนไข่เริ่มแห้ง\r\n2.	นำข้าวสวยลงไปผัดให้เข้ากัน จากนั้นปรุงรสด้วย น้ำตาล ซอสปรุงรส และซอสน้ำมันหอย\r\n3.	นำต้นหอมและมะเขือลงไปคลุกเคล้ากับข้าวผัดและเสริฟใส่จาน แต่งหน้าด้วยต้นหอมและแตงกวา ทานคู่กับพริกน้ำปลา\r\n', '	ข้าวสวย 1 ถ้วยตวง\r\n	เนื้อปู 4 ช้อนโต้ะ\r\n	ต้นหอมซอย 2 ช้อนชา\r\n	มะเขือเทศหั่น 2 ช้อนโต้ะ\r\n	ไข่ไก่ 1 ฟอง\r\n	น้ำตาล 1 ช้อนชา\r\n	ซอสน้ำมันหอย 1 ช้อนชา\r\n	ซอสปรุงรส 1 ช้อนโต้ะ\r\n	น้ำมันพืช 2 ช้อนโต้ะ\r\n	แตงกวาหั่น 3 ชิ้น และต้นหอม\r\n', 'Fs20'),
('f100', 'น้ำพริกปักษ์ใต้', 'C05', 'r4', '1.	นำกะปิไปเผาไฝให้สุก จากนั้นนำกะปิ ไปโขรกกับ พริกสด และหอมแดง ให้เข้ากัน\r\n2.	นำเนื้อกุ้งไปผัดให้สุก จากนั้นนำเนื้อกุ้งไปผสมกับกะปิที่โขรกไว้แล้ว ปรุงรสด้วยน้ำตาลและเกลือ ผสมให้ส่วนผสมเข้ากัน\r\n3.	เสริฟน้ำพริกปักษ์ใต้ ทานกับผักสดและข้าวสวยร้อนๆ กับข้าวง่ายๆ แบบอาหารใต้\r\n', '	เนื้อกุ้ง 1 ช้อนโต้ะ\r\n	เกลือ 1 ช้อนชา\r\n	พริกสด 10 เม็ด\r\n	หอมแดงซอย 3 หัว\r\n	กะปิ 2 ช้อนโต้ะ\r\n	น้ำตาลปี๊บ 1 ช้อนโต้ะ\r\n', 'Fs21'),
('f101', 'แกงไตปลา', 'C04', 'r4', '1.	นำกะปิไปเผาไฝให้สุก จากนั้นนำกะปิ ไปโขรกกับ พริกสด และหอมแดง ให้เข้ากัน\r\n2.	นำเนื้อกุ้งไปผัดให้สุก จากนั้นนำเนื้อกุ้งไปผสมกับกะปิที่โขรกไว้แล้ว ปรุงรสด้วยน้ำตาลและเกลือ ผสมให้ส่วนผสมเข้ากัน\r\n3.	เสริฟน้ำพริกปักษ์ใต้ ทานกับผักสดและข้าวสวยร้อนๆ กับข้าวง่ายๆ แบบอาหารใต้\r\n', '	ไตปลา ครึ่ง ถ้วย\r\n	กุ้งแช่สด 1 ถ้วย\r\n	ปลาดุกย่าง(เอาแต่เนื้อ) 1 ถ้วย\r\n	ถั่วฝักยาว (หั่นท่อนๆ ประมาณ 1 นิ้ว) 1 ถ้วย\r\n	หน่อไม้ปีบ(หั่นเป็นชิ้นพอคำ) 1 ถ้วย\r\n	ส้มแขก 3 ชิ้น\r\n	น้ำมะขามเปียก 2 ช้อนโต้ะ\r\n	ใบมะกรูดหั่นฝอย 1 ช้อนโต้ะ\r\n	ฟักทอง(หั่นชิ้นพอคำ) ครึ่งถ้วย\r\n	น้ำ 1 หม้อ\r\n	พริกแห้ง 1 ถ้วย\r\n	พริกสด 10 เม็ด\r\n	ข่า 1 หัว\r\n	ตะไคร้ 2-3 ต้น\r\n	ผิวมะกรูด 1 ช้อนโต๊ะ\r\n	พริกไทยสด 1 ช้อนโต้ะ\r\n	กระเทียมบด 2 ช้อนโต้ะ\r\n	หอมแดงซอย 2 ช้อนโต้ะ\r\n	ขมิ้นซอย 1 ช้อนโต๊ะ\r\n	กะปิ 2 ช้อนโต๊ะ\r\n	เกลือ 1 ช้อนชา\r\n', 'Fs22'),
('f102', 'ต้มยำปลาทู', 'C01', 'r4', '1.	ต้มน้ำซุปไฟอ่อนๆ จากนั้นใส่ ตะไคร้และข่าลงไปต้มให้น้ำหอมระเหยออกมา\r\n2.	ใส่ปลาทูลงไปต้มให้สุก\r\n3.	ใส่ใบมะกรูด เห็ดฟาง มะเขือเทศและหอมแดงลงไป ปรุงรสด้วยน้ำปลา น้ำมะขามเปียกและพริกสด\r\n4.	ปิดไฟลง และใส่น้ำมะนาวและใบผักชีฝรั่ง ชิมรสตามใจชอบ และเสริฟใส่ชามทานกับข้าวสวย กับข้าวไทย เมนูง่ายๆ ตามร้านอาหารไทย\r\n', '	ปลาทู 2 ตัวพริกสดบด 1 ชอนโต้ะ\r\n	น้ำมะขามเปียก 1 ช้อนโต้ะ\r\n	น้ำมะนาว 2 ช้อนโต้ะ\r\n	มะเขือเทศหั่นครึ่ง 3 ผล\r\n	หอมแดงหั่น 4 ซีก 3 หัว\r\n	ตะไคร้ซอย 1 ต้น\r\n	เห็ดฟางหั่น 1 ถ้วย\r\n	ข่าซอย 1 หัว\r\n	ใบมะกรูด 4-5 ใบ\r\n	ผักชีฝรั่งซอยหยาบๆ 2 ช้อนโต้ะ\r\n	น้ำซุปหมู 1 หม้อ\r\n', 'Fs23'),
('f103', 'แกงส้มปลาช่อนผักรวม', 'C04', 'r4', '	ปลาช่อนหั่นเป็นท่อนๆ 1 ถ้วย\r\n	น้ำพริกแกงส้ม 1 ช้อนโต้ะ\r\n	ผักกวางตุ้งหั่นทอน 200 กรัม\r\n	หัวไชเท้าหั่น 2 ชิ้น\r\n	ถั่วฝักยาวหั่นพอคำ 5 ช้อนโต้ะ\r\n	น้ำเปล่า 2 ถ้วย\r\n	น้ำตาลปี๊บ 1 ช้อนชา\r\n	น้ำปลา 2 ช้อนโต๊ะ\r\n	น้ำมะขามเปียก 3 ช้อนโต๊ะ\r\n	ผักกาดขาว หั่นเป้นชิ้นพอคำ\r\n', '1.	ต้มน้ำให้เดือด จากนั้นใส่ปลาช่อนลงไปต้มจนสุก\r\n2.	นำปลาช่อนพักไว้ก่อน และแกะเนื้อออกมาโขรกกับน้ำพริกแกงส้มให้เข้ากัน\r\n3.	นำน้ำพริกแกงที่โขรกกับเนื้อปลาช่อน ลงไปต้ม จากนั้นเพิ่มไฟให้แรงจัด ปรุงรสด้วยน้ำปลา น้ำตาล และน้ำมะขามเปียก\r\n4.	ใส่ผักที่เตรียมไว้ลงไปต้ม และเสริฟใส่ชาม ทานกับข้าวสวยร้อนๆ\r\n', 'Fs24'),
('f104', 'ปลาหมึกผัดไข่เค็ม', 'C02', 'r4', '1.ตั้งกระทะน้ำมันให้ร้อน นำกระเทียมและปลาหมึกไปผัดสัก 10 วินาที\r\n2.เติมไข่เค็ม พริกหยวก พริกบด หอมใหญ่ น้ำตาล น้ำปลา น้ำมันหอย ซีอิ้วขาว ผัดให้เข้ากันจนสุก\r\n3.เสริฟใส่จาน ทานกับข้าวสวย กับข้าวง่ายๆ\r\n', 'ปลาหมึกหั่นเป็นชิ้น\r\nไข่เค็มต้มซอย 1 ฟอง\r\nหอมใหญ่ซอย\r\nพริกบด 1 ช้อนโต้ะ\r\nกระเทียมบด 1 ช้อนโต้ะ\r\nพริกหยวกหั่นชิ้น\r\nน้ำมันพืช 2 ช้อนโต้ะ\r\nน้ำตาล 1 ช้อนโต้ะ\r\nน้ำปลา 1 ช้อนโต้ะ\r\nน้ำมันหอย 1 ช้อนโต้ะ\r\nซีอิ้วขาว 1 ช้อนโต้ะ\r\n', 'Fs25'),
('f105', 'ต้มโคล้งทะเล', 'C01', 'r4', '1.ต้มน้ำซุปให้เดือด จากนั้นใส่ หอมแดง เห็ด ตะไคร้และใบมะกรูดลงไปต้ม จากนั้นตามด้วย กุ้ง ปลา หมึก และหอย\r\n2. ปรุงรสด้วยน้ำมะขามเปียก เกลือ พริกและน้ำมะนาว เสรืฟพร้อมรับประทาน กับข้าวง่ายๆ\r\n', 'กุ้ง 3 ตัว\r\nปลาหมึกหั่นพอคำ 1 ตัว\r\nหอยแมลงภู่ 4 ตัว\r\nเนื้อปลาหั่นพอคำ 4 ชิ้น\r\nน้ำ ซุปหมู 4 ถ้วยตวง\r\nเห็ดโคนหั่น 4-5 หัว\r\nน้ำมะขามเปียก 2 ข้อนโต้ะ\r\nหอมแดง 3 หัว\r\nเกลือ 1 ช้อนชา\r\nพริกแห้งเผา 4 เม็ด\r\nตะไคร้บุบ 3-4 ท่อน\r\nใบมะกรูดฉีก 2-3 ใบ\r\nน้ำมะนาว 2 ช้อนโต้ะ\r\n', 'Fs26'),
('f106', 'ไก่ทอดหาดใหญ่', 'C03', 'r4', '1. หมักไก่ในชามด้วย พริกไทย ซีอิ้วขาว น้ำปลา ผงรสดี กระเทียมบด ผงขมิ้นและ น้ำตาล หมักไว้ประมาณ 3 ชั่วโมง\r\n2. โรยแป้งสาลีลงไปในไก่ที่หมักไว้\r\n3. ตั้งกะทะ น้ำมัน ให้ร้อนจัด จากนั้นให้หรี่ไฟอ่อนๆ ก่อนทอด เทคนิคคือ ต้องทอดด้วยไฟอ่อนแต่น้ำมันร้อน\r\n4. นำไก่ลงไปทอดประมาณ 15-20 นาที สังเกตุจากสีของไก่ให้ออกเหลืองทอง\r\n5. นำไก่ออกมาพักสะเด็ดน้ำมัน\r\n6. นำหอมแดงที่หั่นเป็นฝอย ลงไปเจียว ใช้ไฟอ่อนอย่าให้หอมไหม้\r\n7. เสริฟไก่ พร้อมด้วยน้ำจิ้มไก่แม่ประนอม โรยหน้าด้วยหอมเจียว ทานกับข้าวเหนียว กับข้าวง่ายๆ เมนูคนใต้\r\n', 'ปีกไก่ 1 กิโลกรัม\r\nพริกไทยแห้งบด 2 ช้อนโต๊ะ\r\nซีอิ้วขาว 2 ช้อนโต้ะ\r\nน้ำปลา 1 ช้อนโต๊ะ\r\nเกลือ 2 ช้อนชา\r\nกระเทียมบด 2 ช้อนโต้ะ\r\nน้ำมันหอย 6 ช้อนโต๊ะ\r\nน้ำตาล 5 ช้อนโต๊ะ\r\nผงขมิ้น 1 ช้อนโต๊ะ\r\nแป้งสาลี 4 ช้อนโต๊ะ\r\nผงรสดี 1 ช้อนโต๊ะ\r\nน้ำมันพืช 3 ถ้วย\r\nหอมแดงหั่นบางๆ 1 ถ้วย\r\nน้ำจิ้มไก่แม่ประนอม 1 ถ้วย\r\n', 'Fs37'),
('f107', 'ผัดฉ่าทะเล', 'C02', 'r4', '	ปลาหมึกหั่นเป็นชิ้น 1 ถ้วย\r\n	เนื้อกุ้ง 1 ถ้วย\r\n	เนื้อปลากะพงหั่น 1 ถ้วย\r\n	พริกไทยสด 1 ช้อนชา\r\n	น้ำมันหอย 1 ช้อนโต้ะ\r\n	รากผักชีบด 1 ช้อนชา\r\n	กระเทียมบด 1 ช้อนโต๊ะ\r\n	น้ำมัน 2 ช้อนโต้ะ\r\n	น้ำปลา 2 ช้อนโต๊ะ\r\n	น้ำตาล 1 ช้อนชา\r\n	คื่นฉ่ายหั่น 2 ช้อนโต้ะ\r\n	ต้นหอมหั่น 2 ช้อนโต้ะ\r\n	พริกสดซอย 1 ช้อนโต้ะ\r\n', '1.	ตั้งกระทะน้ำมัน ให้ร้อน นำกระเทียม รากผักชี ผัดให้สุก จากนั้นนำปลาหมึก กุ้ง และเนื้อปลา ลงไปผัดให้สุก\r\n2.	ปรุงรสด้วย น้ำมันหอย น้ำปลาและน้ำตาล\r\n3.	นำพริกสดซอย คื่นฉ่าย และต้นหอม ลงไปผัดและเสริฟใส่จาน\r\n', 'Fs28'),
('f108', 'กุ้งสะดุ้งไฟ', 'C03', 'r4', '1.	ทำความสะอาดกุ้งให้สะอาด จากนั้นฝ่าหลัง นำไม้ลูกชิ้นมาเสียบที่ตัวกุ้งเพื่อตามตัวกุ้งให้ตัวตรงไม่หดงอเวลาโดนความร้อนจากนั้นนำไปหมักกับเหล้า\r\n2.	ตั้งกระทะให้ร้อนจากนั้นนำกุ้งลงไปหัดในกระทะร้อนๆ ให้ระวังเพราะจะเกิดเปรวไฟสูงควรทำในที่โล่ง สะดุ้งไฟสักครู่ให้กุ้งสุก ก็นำออกมาพักใส่จาน เอาไม้ออกจากตัวกุ้ง จากนั้นนำไปจัดใส่จานเตรียมรับประทาน จากนั้นขั้นตอนต่อไปทำน้ำราดตัวกุ้ง\r\n3.	นำ น้ำมะขามเปียก น้ำตาลทราย น้ำตาลปี๊บ ซอสหอยนางรม ซอสปรุงรส เกลือ และซีอิ้วดำ ผสมให้เข้ากัน จากนั้นนำไปเคี่ยวในหม้อความร้อนปานกลาง พอส่วนผสมง่วนได้ที่ก็ใส่รากผักชีและต้นหอมลงไปเคียวด้วย ได้น้ำซอสราดกุ้งแล้ว\r\n4.	เสริฟกุ้งสะดุ้งไฟ ราดด้วยซอส ทานคู่กับน้ำจิ้มซีฟู๊ด\r\n', '	กุ้งแม่น้ำ ตัวใหญ่ 3 ขีด 3 ตัว\r\n	น้ำมะขามเปียก 6 ช้อนโต้ะ\r\n	น้ำตาลทราย 6 ช้อนโต้ะ\r\n	น้ำตาลปี๊บ 2 ช้อนโต้ะ\r\n	ซอสหอยนางรม 2 ช้อนโต้ะ\r\n	ซอสปรุงรส 6 ช้อนโต้ะ\r\n	เกลือ 1 ช้อนชา\r\n	ซีอิ้วดำ 1 ช้อนโต้ะ\r\n	พริกไทย 1 ช้อนโต้ะ\r\n	รากผัดชี 2 ต้น\r\n	เหล้า 2 ช้อนโต้ะ\r\n	ต้นหอมซอยหยาบๆ 1 ต้น\r\n	น้ำจิ้มซีฟู๊ด 1 ถ้วย\r\n', 'Fs29'),
('f109', 'ยำหนวดปลาหมึก', 'C06', 'r4', '1.	ตั้งหม้อต้มน้ำ ให้เดือด จากนั้นนำหนวดปลาหมึกลงไปลวก จากนัั้นนำไปพักไว้กก่อน\r\n2.	เตรียมน้ำยำ โดย ผสม น้ำปลา น้ำตาล น้ำมะนาว พริก เกลือ และน้ำซุป ผสมให้ส่วนผสมละลายเข้ากัน\r\n3.	ผสมยำหนวดปลาหมึก โดย เตรียมภาชนะ ใส่ หนวดปลาหมึกลวก หอมแดงซอย หอมใหญ่ซอย คื่นฉ่าย มะเขือ ราดน้ำยำลงไป จากนั้นคลุกให้น้ำยำเข้ากับส่วนผสมของเครื่องยำทั้งหมด\r\n', '1.	หนวดปลาหมึก หั่นเป็นชิ้นพอคำ 1 ถ้วย\r\n2.	น้ำซุป 5 ช้อนโต้ะ\r\n3.	หอมหัวใหญ่ซอย 2 ช้อนโต้ะ\r\n4.	มะเขือเทศหั่นเป็นชิ้น 1 ลูก\r\n5.	คืนฉ่ายหั่นพอคำ 2 ช้อนโต้ะ\r\n6.	หอมแดงซอย 1 ช้อนโต้ะ\r\n7.	น้ำปลา 2 ช้อนโต้ะ\r\n8.	น้ำมะนาว 2 ช้อนโต้ะ\r\n9.	น้ำตาล 2 ช้อนโต้ะ\r\n10.	พริกสดบด 1 ช้อนโต้ะ\r\n11.	เกลือ 2 ช้อนโต้ะ\r\n\r\n', 'Fs3'),
('f110', 'หอยแมลงภู่อบสมุนไพร', 'C01', 'r4', '1.	ทำความสะอาดหอยแมลงภู่ให้สะอาด และเอาขนหอยแมลงภู่ออกด้วย เพราะถ้าไม่เอาออกจะทำให้ท้องเสีย เพราะไม่สะอาด ขั้นตอนนี้สำคัญต้องทำความสะอาดหอยให้สะอาดจริงๆ จากนั้นนำหอยมาพักไว้ก่อน\r\n2.	ต้มน้ำซุปใส่หม้อ ใส่ ข่า ตะไคร้ ใบมะกรูด ผักชีฝรั่ง และน้ำปลาลงไปต้ม จากนั้นนำหอยลงไปต้ม สังเกตุตัวหอยพองก็แสดงว่าสุกพร้อมรับประทาน\r\n3.	เสริฟหอยแมลงภู่อบสมุนไพร พร้อมด้วยน้ำจิ้มซีฟู๊ด \r\n', '	หอยแมลงภู่ 1 กิโลกรัม\r\n	ตะไคร้ซอย 2 ช้อนโต้ะ\r\n	ข่าซอย 2 ช้อนโต้ะ\r\n	ใบมะกรูดซอย 2 ช้อนโต้ะ\r\n	น้ำปลา 2 ช้อนโต้ะ\r\n	ผักชีฝรั่งซอยหยาบๆ 2 ช้อนโต้ะ\r\n	น้ำซุป 1 ถ้วยตวง\r\n	น้ำจิ้มซีฟู๊ด 1 ถ้วยน้ำจิ้ม \r\n', 'Fs30'),
('f111', 'น้ำพริกระกำ', 'C05', 'r4', '1.	โขรก กุ้งแห้ง พริกสด และกระเทียมให้ละเอียด จากนั้นตามด้วย น้ำตาล กะปิ และน้ำปล่า\r\n2.	จากนั้นใส่เนื้อระกำลงไปโขลกให้ละเอียด\r\n3.	เสริฟน้ำพริกระกำ อาหารสุขภาพ ทานคู่กับ ผักสด ตามใจชอบ \r\n', '     พริกขี้หนู 10-20 เม็ด\r\n	เนื้อระกำฝาน 1 ถ้วย\r\n	กุ้งแห้ง 3 ช้อนโต้ะ\r\n	กระเทียมสด 5 กลีบ\r\n	น้ำตาลปี๊บ 2 ช้อนโต้ะ\r\n	น้ำปลา 2 ช้อนโต๊ะ\r\n	กะปิ 1 ช้อนโต้ะ\r\n', 'Fs31'),
('f112', 'แกงคั่วกะทิหอยแครง', 'C04', 'r4', '1.	ลวกหอยแครง ด้วยการต้มน้ำให้เดือดจัดใส่หอยแครงลงไปลวก 1 นาที และนำขึ้นมาน็อคน้ำเย็น จากนั้นแกะเอาแต่เนื้อหอยแคลงออกมาและพักไว้ก่อน\r\n2.	โขรกพริกแกง โดย โขรก พริกแห้ง พริกไทย ข่า ตะไคร้ หอมแดง ขมิ้น กระเทียม ผิวมะกรูด และกะปิ โขรกให้ส่วนผสมละเอียดเป็นเนื้อเดียวกัน นำมาพักไว้ก่อน\r\n3.	ตั้งกระทะให้ร้อน นำหัวกะทิลงไปคั่ว จากนั้นใส่พริกแกงลงไปต้มให้แตกมัน จากนั้นใส่หางกะทิลงไป ปรุงรสด้วยน้ำตาลปี๊บ น้ำปลา และผงยี่หร่า จากนัั้นใส่ใบชะพลูลงไป ตามด้วยหอยแครงและพริกสด\r\n4.	เสริฟแกงคั่วกะทิหอยแครง อาหารสุขภาพ อุดมไปด้วยสมุนไพรที่มีประโยชน์ต่อสุขภาพ\r\n', '1.	ตะไคร้ซอย 2 ช้อนโต้ะ\r\n2.	ขมิ้นซอย 2 ช้อนชา\r\n3.	ผิวมะกรูดซอย 1 ช้อนชา\r\n4.	กระเทียมซอย 2 ช้อนโต้ะ\r\n5.	กะปิ 2 ช้อนโต้ะ\r\n6.	หอยแครง 2 ถ้วย\r\n7.	หัวกะทิ 1 ถ้วยตวง\r\n8.	หางกะทิ 1 ถ้วยตวง\r\n9.	น้ำตาลปี๊บ 2 ช้อนโต้ะ\r\n10.	น้ำปลา 2 ช้อนโต้ะ\r\n11.	ผงยี่หร่า 1 ช้อนชา\r\n12.	ใบชะพลู ซอยหยาบ 4 ช้อนโต้ะ\r\n13.	พริกหยวกซอย 2 ช้อนโต้ะ\r\n', 'Fs4'),
('f113', 'หอยหวานผัดพริกแห้ง', 'C02', 'r4', '1.	นำหอยหวานลงไปลวกในน้ำเดือด เมื่อหอยสุก นำไปใส่น้ำเย็น จากนั้นแกะเอาตัวเนื้อหอยออกมาพักใส่จานไว้ก่อน\r\n2.	ตั้งกระทำน้ำมัน ร้อนหน่อย ใส่หอมแดงลงไปผัด พอเหลือง ใส่พริกแห้งลงไปผัด ตามด้วยกระเทียม ผัดให้กระเทียมมีกลิ่นหอม\r\n3.	ใส่เนื้อหอยลงไปผัด ปรุงรสด้วย น้ำตาล น้ำปลา ซอสปรุงรส ซอสน้ำมันหอย พริกไทย ผัดให้ส่วนผสมเข้ากัน เติมน้ำซุปลงไป เพื่อให้ส่วนผสมละลายเข้าเนื้อ\r\n4.	ใส่ใบโหระพาและกะทิลงไปผัดให้มีกลิ่นหอม เสริฟใส่จานพร้อมรับประทานได้\r\n', '1.	หอยหวาน 1 ถ้วย\r\n2.	น้ำพริกเผา 2 ช้อนโต้ะ\r\n3.	น้ำตาล 1 ช้อนชา\r\n4.	พริกแห้งหั่นเป็นชิ่นหยาบๆ 2 ช้อนโต้ะ\r\n5.	น้ำปลา 1 ช้อนชา\r\n6.	ซอสปรุงรส 1 ช้อนชา\r\n7.	ซอสน้ำมันหอย 1 ช้อนชา\r\n8.	กระเทียมบด 1 ช้อนโต้ะ\r\n9.	หอมแดงซอย 2 ช้อนโต้ะ\r\n10.	พริกไทยป่น 1 ช้อนโต้ะ\r\n11.	กะทิ 2 ช้อนโต้ะ\r\n12.	ใบโหระพา 1 กำมือ\r\n', 'Fs5'),
('f114', 'แกงมัสมั่นไก่', 'C04', 'r4', '1.	ตั้งกระทะใส่หัวกะทิเคี้ยวให้แตกมันก่อนจากนั้นใส่พริกแกงมัสมั่นลงไปผัด จากนั้นปรุงรสด้วย น้ำปลา น้ำตาลและน้ำมะขามเปียก\r\n2.	ใส่หางกะทิลงไป ชิมรส ให้ออกหวานนำตามด้วยเค็ม จากนั้นใส่เนื้อไก่ลงไปต้ม ใส่หอมหัวใหญ่ มันฝรั่งลงไป\r\n3.	เคี่ยวต่อด้วยไฟอ่อนๆ สุดท้ายใส่ถั่วลิสงลงไปก็สามารถรับประทานได้\r\n', '1.     เนื้อไก่ส่วนอก 5-6 ชิ้น ตามใจชอบ\n2.	พริกแกงมัสมั่น 5 ช้อนโต้ะ\n3.	มันฝรั่งต้มสุก 5-6 ชิ้น\n4.	หอมหัวใหญ่ หั่นเป็นชิ้นใหญ่ๆ 2 ลูก\n5.	ถั่วลิสงคั่ว 2 ช้อนโต้ะ\n6.	น้ำปลา 2 ช้อนโต้ะ\n7.	น้ำตาลปี๊บ 3 ช้อนโต้ะ\n8.	น้ำมะขามเปียก 1 ช้อนโต้ะ\n9.	หัวกะทิ 1 ถ้วย\n10.	หางกะทิ 4 ถ้วย\n', 'Fs6'),
('f115', 'กุ้งผัดสะตอพริกแกงใต้', 'C02', 'r4', '1.	ตั้งกระทะน้ำมันให้ร้อน นำกุ้งลงไปผัดให้สุก จากนั้นใส่พริกแกงใต้ลงไปผัดให้สุก\r\n2.	ปรุงรสด้วย น้ำตาลและน้ำปลา ใส่สะตอลงไปผัด เติมน้ำลงไปนิดหน่อย\r\n3.	ผัดต่อให้สะตอสุก เสริฟใส่จาก พร้อมรับประทาน กับข้าวง่ายๆ เมนูผัด\r\n', '1.	สะตอ 3 ช้อนโต้ะ\r\n2.	กุ้งสด 10 ตัว\r\n3.	พริกแกงใต้ 1 ช้อนโต้ะ\r\n4.	น้ำตาลปี๊บ 1 ช้อนโต้ะ\r\n5.	น้ำปลา 1 ช้อนโต้ะ\r\n6.	น้ำมันพืชสำหรับผัด\r\n', 'Fs7'),
('f116', 'ปูผัดพริก', 'C02', 'r4', '1.ล้างปูให้สะอาดจากนั้นนำเนื้อปูไปนึ่งให้สุกก่อน\r\n2.ตั้งกระทะน้ำมันให้ร้อน ใส่กระเทียมลงไปผัด จากนั้นใส่ ซอสหอยนางรม ซอสถั่วเหลือง น้ำตาล พริกไทยดำและถั่วฝักยาว ผัดให้ส่วนผสมเข้ากัน\r\n3.. ใส่พริกหยวก ต้นหอม หอมใหญ่ และเนื้อปูลงไปผัด คั่วให้ปลาหมึกสุก จากนั้นเสริฟใส่จานได้ กับข้าวง่ายๆ\r\n', 'เนื้อปู 1 ถ้วย\r\n2.	พริกหยวกสดหั่นเป็นชิ้น 2 เม็ด\r\n3.	ถั่วผักยาวหั่นเป็นท่อนพอคำ 4 ช้อนโต้ะ\r\n4.	หอมหัวใหญ่ซอย 2 ช้อนโต้ะ\r\n5.	ต้นหอมซอยหยาบ 1 ต้น\r\n6.	กระเทียมบด 1 ช้อนโต้ะ\r\n7.	ซอสหอยนางรม 1 ช้อนโต้ะ\r\n8.	น้ำตาล 1 ช้อนชา\r\n9.	ซอสถั่วเหลือง 1 ช้อนโต้ะ\r\n10.	พริกไทยดำบด 1 ช้อนชา\r\n', 'Fs8'),
('f117', 'ปูทอดกระเทียมพริกไทย', 'C03', 'r4', '1.	นำปูมาหมักด้วย กระเทียม ซอสน้ำมันหอย น้ำตาล พริกไทย และแป้งมัน\r\n2.	ตั้งกระทะน้ำมันให้ร้อน จากนั้นปูลงไปทอด สังเกตุให้เหลืองสุก จากนั้นนำมาพักสะเด็ดน้ำมัน เสร็จพร้อมรับประทาน กับข้าว ง่ายๆ เมนูปู\r\n', '1.	ปู 1 ตัว หั่นเป็นชิ้นพอคำ\r\n2.	กระเทียมบด 2 ช้อนโต้ะ\r\n3.	ซอสน้ำมันหอย 1.5 ช้อนโต้ะ\r\n4.	น้ำตาล 1 ช้อนชา\r\n5.	พริกไทย 2 ช้อนชา\r\n6.	แป้งมัน 1 ช้อนโต้ะ\r\n', 'Fs9');
